"use client";

/**
 * ResponsiveLayout - v7.1 ç»Ÿä¸€å“åº”å¼å¸ƒå±€
 *
 * ä½¿ç”¨ CSS åª’ä½“æŸ¥è¯¢ç»Ÿä¸€å“åº”å¼ï¼Œæ¶ˆé™¤åŒæ¸²æŸ“æ¨¡å¼
 * Vercel rule: rerender-animation-library (ä½¿ç”¨ CSS æ›¿ä»£ Framer Motion)
 *
 * å¸ƒå±€è§„èŒƒ:
 * - PC â‰¥1024px: Sidebar(240px) + Main + SidePanel(320px)
 * - Tablet 768-1024px: Sidebar(72px) + Main + å¯æŠ˜å  SidePanel
 * - Mobile <768px: Header + Main + BottomNav
 */

import { memo, ReactNode, createContext, useContext, useState, useCallback } from "react";
import { cn } from "@/lib/utils";

// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
// Context
// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•

interface ResponsiveLayoutContextValue {
  sidebarCollapsed: boolean;
  toggleSidebar: () => void;
  sidePanelOpen: boolean;
  toggleSidePanel: () => void;
}

const ResponsiveLayoutContext = createContext<ResponsiveLayoutContextValue | null>(null);

export function useResponsiveLayout() {
  const context = useContext(ResponsiveLayoutContext);
  if (!context) {
    throw new Error("useResponsiveLayout must be used within ResponsiveLayout");
  }
  return context;
}

// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
// ResponsiveLayout Component
// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•

interface ResponsiveLayoutProps {
  children: ReactNode;
  sidebar?: ReactNode;
  sidePanel?: ReactNode;
  header?: ReactNode;
  bottomNav?: ReactNode;
  className?: string;
}

export const ResponsiveLayout = memo(function ResponsiveLayout({
  children,
  sidebar,
  sidePanel,
  header,
  bottomNav,
  className,
}: ResponsiveLayoutProps) {
  const [sidebarCollapsed, setSidebarCollapsed] = useState(false);
  const [sidePanelOpen, setSidePanelOpen] = useState(true);

  const toggleSidebar = useCallback(() => {
    setSidebarCollapsed(prev => !prev);
  }, []);

  const toggleSidePanel = useCallback(() => {
    setSidePanelOpen(prev => !prev);
  }, []);

  const contextValue: ResponsiveLayoutContextValue = {
    sidebarCollapsed,
    toggleSidebar,
    sidePanelOpen,
    toggleSidePanel,
  };

  return (
    <ResponsiveLayoutContext.Provider value={contextValue}>
      <div className={cn("min-h-screen bg-bg-primary", className)}>
        {/* Mobile Header - ä»… <768px æ˜¾ç¤º */}
        {header && (
          <div className="md:hidden sticky top-0 z-40">
            {header}
          </div>
        )}

        <div className="flex min-h-screen md:h-screen">
          {/* Sidebar - ä»… â‰¥768px æ˜¾ç¤º */}
          {sidebar && (
            <aside
              className={cn(
                "hidden md:flex flex-col",
                "fixed left-0 top-0 bottom-0 z-30",
                "bg-bg-card shadow-card",
                "transition-[width] duration-slow ease-out-expo",
                sidebarCollapsed ? "w-[72px]" : "w-[240px]",
                "lg:w-[240px]" // å¤§å±å§‹ç»ˆå±•å¼€
              )}
            >
              {sidebar}
            </aside>
          )}

          {/* Main Content */}
          <main
            className={cn(
              "flex-1 flex flex-col",
              "transition-[margin] duration-slow ease-out-expo",
              // ç§»åŠ¨ç«¯æ— è¾¹è·
              "md:ml-[72px]",
              !sidebarCollapsed && "md:ml-[240px]",
              "lg:ml-[240px]" // å¤§å±å§‹ç»ˆ 240px
            )}
          >
            <div className="flex-1 flex overflow-hidden">
              {/* ä¸»å†…å®¹åŒº */}
              <div className="flex-1 overflow-y-auto">
                {children}
              </div>

              {/* Side Panel - ä»… â‰¥1024px æ˜¾ç¤º */}
              {sidePanel && (
                <aside
                  className={cn(
                    "hidden lg:block",
                    "w-[320px] xl:w-[360px]",
                    "bg-bg-card shadow-card",
                    "border-l border-subtle/50",
                    "overflow-y-auto",
                    "transition-all duration-slow ease-out-expo",
                    !sidePanelOpen && "lg:w-0 lg:opacity-0 lg:overflow-hidden"
                  )}
                >
                  {sidePanel}
                </aside>
              )}
            </div>
          </main>
        </div>

        {/* Mobile Bottom Nav - ä»… <768px æ˜¾ç¤º */}
        {bottomNav && (
          <div className="md:hidden fixed bottom-0 left-0 right-0 z-40">
            {bottomNav}
          </div>
        )}
      </div>
    </ResponsiveLayoutContext.Provider>
  );
});

// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
// Sidebar Component
// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•

interface SidebarProps {
  logo?: ReactNode;
  nav?: ReactNode;
  footer?: ReactNode;
  className?: string;
}

export const Sidebar = memo(function Sidebar({
  logo,
  nav,
  footer,
  className,
}: SidebarProps) {
  const { sidebarCollapsed, toggleSidebar } = useResponsiveLayout();

  return (
    <div className={cn("flex flex-col h-full", className)}>
      {/* Logo */}
      {logo && (
        <div className="p-4 flex items-center gap-3">
          {logo}
        </div>
      )}

      {/* Navigation */}
      <nav className="flex-1 px-3 py-2 overflow-y-auto">
        {nav}
      </nav>

      {/* Footer */}
      <div className="p-3 border-t border-subtle/50">
        {footer}
        {/* Collapse Toggle - ä»… tablet æ˜¾ç¤º */}
        <button
          onClick={toggleSidebar}
          className={cn(
            "hidden md:flex lg:hidden",
            "w-full items-center justify-center",
            "p-2 mt-2 rounded-lg",
            "text-text-secondary hover:text-text-primary hover:bg-bg-secondary",
            "transition-colors duration-fast"
          )}
        >
          <span className="text-lg">{sidebarCollapsed ? "â–¶" : "â—€"}</span>
        </button>
      </div>
    </div>
  );
});

