interface SidePanelProps {
  title?: string;
  children: ReactNode;
  className?: string;
}

export const SidePanel = memo(function SidePanel({
  title,
  children,
  className,
}: SidePanelProps) {
  const { toggleSidePanel } = useResponsiveLayout();

  return (
    <div className={cn("h-full flex flex-col", className)}>
      {/* Header */}
      {title && (
        <div className="flex items-center justify-between p-4 border-b border-subtle/50">
          <h2 className="text-lg font-semibold text-text-primary">{title}</h2>
          <button
            onClick={toggleSidePanel}
            className="p-1.5 rounded-lg text-text-tertiary hover:text-text-primary hover:bg-bg-secondary transition-colors"
          >
            <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
              <path d="M18 6 6 18M6 6l12 12" />
            </svg>
          </button>
        </div>
      )}

      {/* Content */}
      <div className="flex-1 overflow-y-auto p-4">
        {children}
      </div>
    </div>
  );
});

