// MobileBottomNav Component
// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•

interface NavItem {
  id: string;
  icon: ReactNode;
  label: string;
  href?: string;
}

interface MobileBottomNavProps {
  items: NavItem[];
  activeId: string;
  onItemClick: (id: string) => void;
  className?: string;
}

export const MobileBottomNav = memo(function MobileBottomNav({
  items,
  activeId,
  onItemClick,
  className,
}: MobileBottomNavProps) {
  return (
    <nav
      className={cn(
        "h-16 flex items-center justify-around",
        "bg-bg-card/95 backdrop-blur-md",
        "border-t border-subtle/50",
        "pb-safe", // iOS safe area
        className
      )}
    >
      {items.map((item) => {
        const isActive = activeId === item.id;
        return (
          <button
            key={item.id}
            onClick={() => onItemClick(item.id)}
            className={cn(
              "flex-1 flex flex-col items-center justify-center gap-1 py-2",
              "transition-colors duration-fast",
              isActive ? "text-accent-primary" : "text-text-tertiary"
            )}
          >
            {/* Active indicator */}
            {isActive && (
              <span className="absolute -top-0.5 w-8 h-1 rounded-full bg-accent-primary" />
            )}
            <span className={cn("text-xl", isActive && "scale-110 transition-transform")}>
              {item.icon}
            </span>
            <span className={cn("text-xs", isActive ? "font-semibold" : "font-medium")}>
              {item.label}
            </span>
          </button>
        );
      })}
    </nav>
  );
});

