"use client";

/**
 * Journey Page - 我的旅程
 *
 * 基于 Dan Koe 一日重置流程的人生规划中心
 * 4状态渐进系统: empty → north_star_set → planned → executing
 *
 * 文档: @vibelife/docs/components/journey/
 */

import { JourneyContent } from "@/components/journey/JourneyContent";

export default function JourneyPage() {
  return (
    <div className="min-h-screen bg-background">
      <JourneyContent />
    </div>
  );
}

