"use client";

/**
 * JourneyContent - Journey 页面主内容
 *
 * 根据 skill_data 状态展示不同内容：
 * - empty: EmptyJourneyCard (引导开始)
 * - north_star_set: NorthStarCard + NorthStarSetCard (引导制定路线图)
 * - planned: NorthStarCard(compact) + YearlyGoalsCard + MonthlyBossCard + PlannedStateCard (引导设定周大石头)
 * - executing: 完整的执行仪表盘
 *
 * 基于: docs/components/ui/journey.md
 */

import { useRouter } from "next/navigation";
import { useCallback } from "react";
import { useJourneyData } from "@/hooks/useJourneyData";
import { EmptyJourneyCard } from "./EmptyJourneyCard";
import { NorthStarCard } from "./NorthStarCard";
import { NorthStarSetCard } from "./NorthStarSetCard";
import { PlannedStateCard } from "./PlannedStateCard";
import { YearlyGoalsCard } from "./YearlyGoalsCard";
import { MonthlyBossCard } from "./MonthlyBossCard";
import { WeeklyActionsCard } from "./WeeklyActionsCard";
import { JourneyDailyLeversCard } from "./JourneyDailyLeversCard";
import { JourneySkeleton } from "./JourneySkeleton";

interface JourneyContentProps {
  className?: string;
}

export function JourneyContent({ className }: JourneyContentProps) {
  const router = useRouter();
  const {
    data,
    isLoading,
    error,
    journeyState,
    northStar,
    identity,
    goals,
    weekly,
    monthlyBoss,
    dailyLevers,
    progress,
    toggleAction,
    toggleLever,
    toggleMilestone,
    checkin,
    refresh,
  } = useJourneyData();

  // ═══════════════════════════════════════════════════════════════════════════
  // Handlers
  // ═══════════════════════════════════════════════════════════════════════════

  // ═══════════════════════════════════════════════════════════════════════════
  // 所有可控入口显式传 scenario 参数，确保正确加载对应的 rule 文件
  // ═══════════════════════════════════════════════════════════════════════════

  const handleStartJourney = useCallback(() => {
    router.push("/chat?skill=lifecoach&scenario=dankoe&prompt=我想做一次人生重置");
  }, [router]);

  const handleEditNorthStar = useCallback(() => {
    router.push("/chat?skill=lifecoach&scenario=dankoe&prompt=我想更新我的愿景");
  }, [router]);

  const handleTalkToFutureSelf = useCallback(() => {
    router.push("/chat?skill=lifecoach&scenario=dankoe&prompt=和未来的我对话");
  }, [router]);

  const handleCreateRoadmap = useCallback(() => {
    router.push("/chat?skill=lifecoach&scenario=covey&prompt=帮我制定年度路线图");
  }, [router]);

  const handleEditGoals = useCallback(() => {
    router.push("/chat?skill=lifecoach&scenario=covey&prompt=我想调整年度目标");
  }, [router]);

  const handleSetWeeklyRocks = useCallback(() => {
    router.push("/chat?skill=lifecoach&scenario=covey&prompt=帮我设定本周大石头");
  }, [router]);

  const handleAddAction = useCallback(() => {
    router.push("/chat?skill=lifecoach&scenario=covey&prompt=帮我添加本周行动");
  }, [router]);

  const handleEditBoss = useCallback(() => {
    router.push("/chat?skill=lifecoach&scenario=covey&prompt=我想调整本月Boss");
  }, [router]);

  const handleAdjustLevers = useCallback(() => {
    router.push("/chat?skill=lifecoach&scenario=covey&prompt=调整今日杠杆");
  }, [router]);

  const handleWeeklyReview = useCallback(() => {
    router.push("/chat?skill=lifecoach&scenario=weekly-review&prompt=周复盘");
  }, [router]);

  const handleStuck = useCallback(() => {
    router.push("/chat?skill=lifecoach&scenario=dankoe&prompt=我卡住了");
  }, [router]);

  const handleCheckin = useCallback(async () => {
    await checkin();
  }, [checkin]);

  // ═══════════════════════════════════════════════════════════════════════════
  // Loading State
  // ═══════════════════════════════════════════════════════════════════════════

  if (isLoading) {
    return <JourneySkeleton />;
  }

  // ═══════════════════════════════════════════════════════════════════════════
  // Error State
  // ═══════════════════════════════════════════════════════════════════════════

  if (error) {
    // Check if it's an authentication error
    const isAuthError = error.message?.includes("Unauthorized") ||
                        error.message?.includes("Authentication required") ||
                        error.message?.includes("401");

    if (isAuthError) {
      return (
        <div className="flex flex-col items-center justify-center min-h-[60vh] px-6 space-y-6">
          <div className="w-20 h-20 rounded-full bg-accent-primary/10 flex items-center justify-center">
            <span className="text-4xl">🔒</span>
          </div>
          <div className="text-center space-y-2">
            <h2 className="text-2xl font-bold text-text-primary">需要登录</h2>
            <p className="text-text-secondary max-w-md">
              Journey 是你的人生规划中心，需要登录后才能访问。
            </p>
          </div>
          <button
            onClick={() => router.push("/auth/login?redirect=/chat")}
            className="px-6 py-3 bg-accent-primary text-text-inverse font-medium rounded-full hover:bg-accent-primary/90 transition-colors"
          >
            立即登录
          </button>
        </div>
      );
    }

    // Other errors
    return (
      <div className="flex flex-col items-center justify-center min-h-[60vh] px-6 space-y-6">
        <div className="w-20 h-20 rounded-full bg-red-500/10 flex items-center justify-center">
          <span className="text-4xl">⚠️</span>
        </div>
        <div className="text-center space-y-2">
          <h2 className="text-2xl font-bold text-text-primary">加载失败</h2>
          <p className="text-text-secondary max-w-md">
            {error.message || "无法加载 Journey 数据，请稍后重试"}
          </p>
        </div>
        <button
          onClick={() => refresh()}
          className="px-6 py-3 bg-accent-primary text-text-inverse font-medium rounded-full hover:bg-accent-primary/90 transition-colors"
        >
          重新加载
        </button>
      </div>
    );
  }

  // ══════════════════════════════��════════════════════════════════════════════
  // State 0: Empty State
  // ═══════════════════════════════════════════════════════════════════════════

  if (journeyState === "empty") {
    return <EmptyJourneyCard onStart={handleStartJourney} className={className} />;
  }

  // ═══════════════════════════════════════════════════════════════════════════
  // State 1: North Star Set - 有北极星，无路线图
  // ═══════════════════════════════════════════════════════════════════════════

  if (journeyState === "north_star_set") {
    return (
      <div className={`space-y-4 p-4 ${className}`}>
        {/* North Star Card */}
        {northStar && (
          <NorthStarCard
            northStar={northStar}
            identity={identity}
            onEdit={handleEditNorthStar}
            onTalkToFutureSelf={handleTalkToFutureSelf}
          />
        )}

        {/* CTA: Create Roadmap */}
        <NorthStarSetCard onCreateRoadmap={handleCreateRoadmap} />
      </div>
    );
  }

  // ═══════════════════════════════════════════════════════════════════════════
  // State 2: Planned - 有路线图，无周大石头
  // ═══════════════════════════════════════════════════════════════════════════

  if (journeyState === "planned") {
    return (
      <div className={`space-y-4 p-4 ${className}`}>
        {/* North Star Card (Compact) */}
        {northStar && (
          <NorthStarCard
            northStar={northStar}
            identity={identity}
            compact
            onEdit={handleEditNorthStar}
          />
        )}

        {/* Yearly Goals Card */}
        {goals.length > 0 && (
          <YearlyGoalsCard goals={goals} onEditGoals={handleEditGoals} />
        )}

        {/* Monthly Boss Card */}
        {monthlyBoss && (
          <MonthlyBossCard
            monthlyBoss={monthlyBoss}
            onToggleMilestone={toggleMilestone}
            onEditBoss={handleEditBoss}
          />
        )}

        {/* CTA: Set Weekly Rocks */}
        <PlannedStateCard onSetWeeklyRocks={handleSetWeeklyRocks} />
      </div>
    );
  }

  // ═══════════════════════════════════════════════════════════════════════════
  // State 3: Executing State - Full Dashboard
  // ═══════════════════════════════════════════════════════════════════════════

  return (
    <div className={`space-y-4 p-4 ${className}`}>
      {/* 1. North Star Card (Compact) */}
      {northStar && (
        <NorthStarCard
          northStar={northStar}
          identity={identity}
          compact
          onEdit={handleEditNorthStar}
          onTalkToFutureSelf={handleTalkToFutureSelf}
        />
      )}

      {/* 2. Yearly Goals Card */}
      {goals.length > 0 && (
        <YearlyGoalsCard goals={goals} onEditGoals={handleEditGoals} />
      )}

      {/* 3. Monthly Boss Card */}
      {monthlyBoss && (
        <MonthlyBossCard
          monthlyBoss={monthlyBoss}
          onToggleMilestone={toggleMilestone}
          onEditBoss={handleEditBoss}
        />
      )}

      {/* 4. Weekly Actions Card */}
      {weekly && (
        <WeeklyActionsCard
          weekly={weekly}
          onToggleAction={toggleAction}
          onAddAction={handleAddAction}
        />
      )}

      {/* 5. Daily Levers Card */}
      {dailyLevers && (
        <JourneyDailyLeversCard
          dailyLevers={dailyLevers}
          todayCheckedIn={progress?.today_checked_in}
          currentStreak={progress?.current_streak}
          onToggleLever={toggleLever}
          onCheckin={handleCheckin}
          onAdjustLevers={handleAdjustLevers}
        />
      )}

      {/* 6. Progress Stats */}
      {progress && (
        <div className="bg-card border border-border rounded-2xl p-4">
          <div className="flex items-center gap-2 mb-3">
            <span className="text-xl">🔥</span>
            <h3 className="font-semibold text-foreground">进度</h3>
          </div>
          <div className="grid grid-cols-3 gap-4 text-center">
            <div>
              <p className="text-2xl font-bold text-foreground">
                {progress.current_streak}
              </p>
              <p className="text-xs text-muted-foreground">连续天数</p>
            </div>
            <div>
              <p className="text-2xl font-bold text-foreground">
                {progress.longest_streak}
              </p>
              <p className="text-xs text-muted-foreground">最长记录</p>
            </div>
            <div>
              <p className="text-2xl font-bold text-foreground">
                {progress.total_checkins}
              </p>
              <p className="text-xs text-muted-foreground">总签到</p>
            </div>
          </div>
          {progress.weekly_completion_rate !== undefined && (
            <div className="mt-3 pt-3 border-t border-border">
              <div className="flex items-center justify-between text-sm">
                <span className="text-muted-foreground">本周完成率</span>
                <span className="font-medium text-foreground">
                  {progress.weekly_completion_rate}%
                </span>
              </div>
            </div>
          )}
        </div>
      )}

      {/* 7. Quick Actions */}
      <div className="bg-card border border-border rounded-2xl p-4">
        <div className="flex items-center gap-2 mb-3">
          <span className="text-xl">💬</span>
          <h3 className="font-semibold text-foreground">需要帮助？</h3>
        </div>
        <div className="flex flex-wrap gap-2">
          <button
            onClick={handleEditNorthStar}
            className="px-3 py-2 text-sm bg-muted hover:bg-muted/80 rounded-lg transition-colors"
          >
            调整目标
          </button>
          <button
            onClick={handleWeeklyReview}
            className="px-3 py-2 text-sm bg-muted hover:bg-muted/80 rounded-lg transition-colors"
          >
            周复盘
          </button>
          <button
            onClick={handleStuck}
            className="px-3 py-2 text-sm bg-muted hover:bg-muted/80 rounded-lg transition-colors"
          >
            我卡住了
          </button>
        </div>
      </div>
    </div>
  );
}

export default JourneyContent;

