"use client";

/**
 * EmptyJourneyCard - 空状态引导卡片
 *
 * 当用户未设置北极星时显示，引导开始 Dan Koe 协议
 * 基于: docs/components/ui/journey.md State 0
 */

import { motion } from "framer-motion";
import { useRouter } from "next/navigation";

interface EmptyJourneyCardProps {
  onStart?: () => void;
  className?: string;
}

export function EmptyJourneyCard({ onStart, className }: EmptyJourneyCardProps) {
  const router = useRouter();

  const handleStart = () => {
    console.log("[EmptyJourneyCard] 开始设计按钮被点击");

    if (onStart) {
      console.log("[EmptyJourneyCard] 使用自定义 onStart 处理器");
      onStart();
    } else {
      const targetUrl = "/chat?skill=lifecoach&scenario=dankoe&prompt=我想做一次人生重置";
      console.log("[EmptyJourneyCard] 跳转到:", targetUrl);

      try {
        router.push(targetUrl);
        console.log("[EmptyJourneyCard] router.push 已调用");
      } catch (error) {
        console.error("[EmptyJourneyCard] router.push 失败:", error);
      }
    }
  };

  return (
    <motion.div
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.5 }}
      className={`flex flex-col items-center justify-center min-h-[60vh] px-6 ${className}`}
    >
      {/* Icon */}
      <motion.div
        initial={{ scale: 0.8 }}
        animate={{ scale: 1 }}
        transition={{ delay: 0.2, type: "spring" }}
        className="text-6xl mb-6"
      >
        🧭
      </motion.div>

      {/* Title */}
      <motion.h2
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ delay: 0.3 }}
        className="text-xl font-semibold text-foreground mb-2 text-center"
      >
        还没有设定方向？
      </motion.h2>

      {/* Subtitle */}
      <motion.p
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ delay: 0.4 }}
        className="text-muted-foreground text-center mb-8 max-w-sm"
      >
        每一段伟大的旅程
        <br />
        都从一个清晰的目的地开始
      </motion.p>

      {/* CTA Button */}
      <motion.button
        initial={{ opacity: 0, y: 10 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ delay: 0.5 }}
        whileHover={{ scale: 1.02 }}
        whileTap={{ scale: 0.98 }}
        onClick={handleStart}
        className="w-full max-w-sm py-4 px-6 bg-primary text-primary-foreground rounded-xl font-medium text-base shadow-lg hover:shadow-xl transition-shadow"
      >
        开始设计我的人生
      </motion.button>

      {/* Time hint */}
      <motion.p
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ delay: 0.6 }}
        className="text-xs text-muted-foreground mt-3"
      >
        预计 10-15 分钟 · 可随时保存
      </motion.p>

      {/* Benefits */}
      <motion.div
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ delay: 0.7 }}
        className="mt-8 pt-6 border-t border-border w-full max-w-sm"
      >
        <p className="text-xs text-muted-foreground mb-3 flex items-center gap-2">
          <span>💡</span>
          <span>这个流程会帮你：</span>
        </p>
        <div className="flex items-center justify-center gap-2 text-xs text-muted-foreground">
          <span>看清你真正恐惧什么</span>
          <span>→</span>
          <span>明确你真正想要什么</span>
          <span>→</span>
          <span>设计实现路径</span>
        </div>
      </motion.div>
    </motion.div>
  );
}

export default EmptyJourneyCard;

