    21→}
    22→
    23→// ═══════════════════════════════════════════════════════════════════════════
    24→// Yearly Goals (年度目标)
    25→// ═══════════════════════════════════════════════════════════════════════════
    26→
    27→export interface YearlyGoal {
    28→  /** 目标 ID */
    29→  id: string;
    30→  /** 目标标题 */
    31→  title: string;
    32→  /** 目标描述 */
    33→  description?: string;
    34→  /** 目标类别 */
    35→  category?: "career" | "health" | "relationship" | "wealth" | "growth" | "other";
    36→  /** 进度 0-100 */
    37→  progress: number;
    38→  /** 状态 */
    39→  status: "pending" | "in_progress" | "completed" | "abandoned";
    40→  /** 目标年份 */
    41→  year?: number;
