/**
 * Journey Types - 基于 CoreAgent SKILL_DATA_ARCHITECTURE
 *
 * 数据结构对应后端 skill_data.lifecoach
 * 参考: docs/components/coreagent/SKILL_DATA_ARCHITECTURE.md
 */

// ═══════════════════════════════════════════════════════════════════════════
// North Star (北极星愿景) - Dan Koe 协议核心输出
// ═══════════════════════════════════════════════════════════════════════════

export interface NorthStar {
  /** 愿景场景 - 3年后的周二 */
  vision_scene?: string;
  /** 愿景时间框架 */
  vision_timeframe?: string;
  /** 反愿景场景 - 5年后的周二 */
  anti_vision_scene?: string;
  /** 持续的不满/痛点 */
  pain_points?: string[];
}

// ═══════════════════════════════════════════════════════════════════════════
// Yearly Goals (年度目标)
// ═══════════════════════════════════════════════════════════════════════════

export interface YearlyGoal {
  /** 目标 ID */
  id: string;
  /** 目标标题 */
  title: string;
  /** 目标描述 */
  description?: string;
  /** 目标类别 */
  category?: "career" | "health" | "relationship" | "wealth" | "growth" | "other";
  /** 进度 0-100 */
  progress: number;
  /** 状态 */
  status: "pending" | "in_progress" | "completed" | "abandoned";
  /** 目标年份 */
  year?: number;
  /** 里程碑 */
  milestones?: Milestone[];
}

export interface Milestone {
  /** 里程碑 ID */
  id: string;
  /** 里程碑标题 */
  title: string;
  /** 是否完成 */
  completed: boolean;
  /** 完成时间 */
  completed_at?: string;
}

// ═══════════════════════════════════════════════════════════════════════════
// Roadmap (路线图)
// ═══════════════════════════════════════════════════════════════════════════

export interface Roadmap {
  /** 年度目标列表 */
  goals?: YearlyGoal[];
  /** 创建时间 */
  created_at?: string;
  /** 更新时间 */
  updated_at?: string;
}

// ══════════════════════��════════════════════════════════════════════════════
// Monthly Boss (月度 Boss)
// ═══════════════════════════════════════════════════════════════════════════

export interface MonthlyBoss {
  /** Boss ID */
  id?: string;
  /** Boss 名称/主题 */
  title: string;
  /** 描述 */
  description?: string;
  /** 月份 YYYY-MM */
  month: string;
  /** 进度 0-100 */
  progress: number;
  /** 状态 */
  status: "pending" | "in_progress" | "completed" | "failed";
  /** 关联的年度目标 ID */
  goal_id?: string;
  /** 里程碑 */
  milestones?: Milestone[];
}

// ═══════════════════════════════════════════════════════════════════════════
// Daily Levers (每日杠杆)
// ═══════════════════════════════════════════════════════════════════════════

export interface DailyLever {
  /** 杠杆 ID */
  id: string;
  /** 任务描述 */
  task: string;
  /** 状态 */
  status: "pending" | "completed" | "skipped";
  /** 完成时间 */
  completed_at?: string;
  /** 跳过原因 */
  skip_reason?: string;
  /** 关联的周大石头 ID */
  rock_id?: string;
}

export interface DailyLevers {
  /** 日期 YYYY-MM-DD */
  date: string;
  /** 杠杆列表 */
  levers: DailyLever[];
  /** 能量等级 1-10 */
  energy_level?: number;
  /** 今日意图 */
  intention?: string;
  /** 统计 */
  stats?: {
    total: number;
    completed: number;
    completion_rate: number;
  };
}

// ═══════════════════════════════════════════════════════════════════════════
// Identity (身份转换) - Dan Koe 协议核心输出
// ═══════════════════════════════════════════════════════════════════════════

export interface Identity {
  /** 旧身份 - "我是那种会..." */
  current?: string;
  /** 新身份 - "我是那种..." */
  target?: string;
  /** 角色列表 */
  roles?: string[];
}

// ═══════════════════════════════════════════════════════════════════════════
// Weekly (本周行动) - Dan Koe 协议核心输出
// ═══════════════════════════════════════════════════════════════════════════

export interface WeeklyAction {
  /** 任务 ID */
  id?: string;
  /** 任务描述 */
  task: string;
  /** 状态 */
  status: "pending" | "in_progress" | "completed";
  /** 进度 0-100 */
  progress?: number;
  /** 完成时间 */
  completed_at?: string;
}

export interface Weekly {
  /** 周开始日期 */
  week_start?: string;
  /** 本周大石头/行动 */
  rocks?: WeeklyAction[];
  /** 本周主题 */
  theme?: string;
  /** 能量预算 */
  energy_budget?: number;
  /** 统计 */
  stats?: {
    total: number;
    completed: number;
    in_progress: number;
    completion_rate: number;
  };
}

// ═══════════════════════════════════════════════════════════════════════════
// System State (系统状态)
// ═══════════════════════════════════════════════════════════════════════════

export interface LifecoachSystemState {
  /** 当前使用的方法论 */
  active_framework?: "dankoe" | "covey" | "yangming" | "liaofan" | null;
  /** 初始化时间 */
  initialized_at?: string;
  /** 最后交互时间 */
  last_interaction?: string;
}

// ═══════════════════════════════════════════════════════════════════════════
// Progress (进度统计)
// ═══════════════════════════════════════════════════════════════════════════

export interface Progress {
  /** 当前连续天数 */
  current_streak: number;
  /** 最长连续天数 */
  longest_streak: number;
  /** 总签到次数 */
  total_checkins: number;
  /** 最后签到日期 */
  last_checkin_date?: string;
  /** 任务完成数 */
  tasks_completed?: number;
  /** 本周完成率 */
  weekly_completion_rate?: number;
  /** 今日是否已签到 */
  today_checked_in?: boolean;
  /** 成就列表 */
  achievements?: string[];
}

// ═══════════════════════════════════════════════════════════════════════════
// LifecoachSkillData - 完整的 skill_data.lifecoach 结构
// ═══════════════════════════════════════════════════════════════════════════

export interface LifecoachSkillData {
  /** 北极星愿景 */
  north_star?: NorthStar;
  /** 身份转换 */
  identity?: Identity;
  /** 路线图 (年度目标) */
  roadmap?: Roadmap;
  /** 年度目标 (兼容旧格式) */
  goals?: YearlyGoal[];
  /** 本周行动 */
  weekly?: Weekly;
  /** 周大石头 (新格式) */
  weekly_rocks?: Weekly;
  /** 月度 Boss */
  monthly_boss?: MonthlyBoss;
  /** 每日杠杆 */
  daily_levers?: DailyLevers;
  /** 进度统计 */
  progress?: Progress;
  /** 系统状态 */
  _state?: LifecoachSystemState;
  /** 元数据 */
  _meta?: {
    version: number;
    created_at?: string;
    updated_at?: string;
  };
}

// ═══════════════════════════════════════════════════════════════════════════
// Journey Page State (页面状态机)
// ═══════════════════════════════════════════════════════════════════════════

/**
 * Journey 页面状态 - 4 状态渐进系统
 * - empty: 未设置北极星，引导开始
 * - north_star_set: 有北极星，无路线图，引导制定路线图
 * - planned: 有路线图，无周大石头，引导设定本周大石头
 * - executing: 有周大石头，展示完整执行仪表盘
 */
export type JourneyState = "empty" | "north_star_set" | "planned" | "executing";

/**
 * 根据 skill_data 判断页面状态
 */
export function getJourneyState(data: LifecoachSkillData | null | undefined): JourneyState {
  // State 0: empty - 没有北极星愿景
  if (!data?.north_star?.vision_scene) {
    return "empty";
  }

  // State 1: north_star_set - 有北极星，但没有路线图/年度目标
  const hasRoadmap = (data.roadmap?.goals?.length ?? 0) > 0 || (data.goals?.length ?? 0) > 0;
  if (!hasRoadmap) {
    return "north_star_set";
  }

  // State 2: planned - 有路线图，但没有周大石头
  const hasWeeklyRocks = (data.weekly?.rocks?.length ?? 0) > 0 || (data.weekly_rocks?.rocks?.length ?? 0) > 0;
  if (!hasWeeklyRocks) {
    return "planned";
  }

  // State 3: executing - 完整执行状态
  return "executing";
}

// ═══════════════════════════════════════════════════════════════════════════
// API Response Types
// ═══════════════════════════════════════════════════════════════════════════

export interface ReadLifecoachStateResponse {
  status: "success" | "empty" | "error";
  message?: string;
  data: LifecoachSkillData;
  version?: number;
  updated_at?: string;
}

// ═══════════════════════════════════════════════════════════════════════════
// show_dankoe Tool Response (Chat 中的卡片数据)
// ═══════════════════════════════════════════════════════════════════════════

export interface DankoeCardData {
  card_type: "dankoe";
  data: {
    vision: {
      statement?: string;
      scene?: string;
      identity?: string;
    };
    anti_vision: {
      statement?: string;
      scene?: string;
    };
    game: {
      win_condition?: string;
      lose_condition?: string;
      main_mission?: string;
      current_boss?: string;
      daily_quests?: string[];
      rules?: string[];
    };
    generated_at?: string;
  };
}

