"use client";

/**
 * YearlyGoalsCard - 年度目标卡片
 *
 * 展示年度目标列表及进度
 * 基于: Journey Page 4-state 系统
 */

import { motion } from "framer-motion";
import type { YearlyGoal } from "@/types/journey";

interface YearlyGoalsCardProps {
  goals: YearlyGoal[];
  onEditGoals?: () => void;
  className?: string;
}

const CATEGORY_ICONS: Record<string, string> = {
  career: "💼",
  health: "💪",
  relationship: "❤️",
  wealth: "💰",
  growth: "🌱",
  other: "🎯",
};

const CATEGORY_COLORS: Record<string, string> = {
  career: "bg-blue-500",
  health: "bg-green-500",
  relationship: "bg-pink-500",
  wealth: "bg-amber-500",
  growth: "bg-purple-500",
  other: "bg-gray-500",
};

export function YearlyGoalsCard({
  goals,
  onEditGoals,
  className,
}: YearlyGoalsCardProps) {
  const currentYear = new Date().getFullYear();
  const yearGoals = goals.filter((g) => !g.year || g.year === currentYear);

  const completedCount = yearGoals.filter((g) => g.status === "completed").length;
  const totalCount = yearGoals.length;
  const overallProgress =
    totalCount > 0
      ? Math.round(yearGoals.reduce((sum, g) => sum + (g.progress || 0), 0) / totalCount)
      : 0;

  if (totalCount === 0) {
    return null;
  }

  return (
    <motion.div
      initial={{ opacity: 0, y: 10 }}
      animate={{ opacity: 1, y: 0 }}
      className={`bg-card border border-border rounded-2xl p-4 ${className}`}
    >
      {/* Header */}
      <div className="flex items-center justify-between mb-3">
        <div className="flex items-center gap-2">
          <span className="text-xl">🎯</span>
          <h3 className="font-semibold text-foreground">{currentYear} 年度目标</h3>
        </div>
        <div className="flex items-center gap-2">
          <span className="text-sm text-muted-foreground">
            {completedCount}/{totalCount}
          </span>
          {onEditGoals && (
            <button
              onClick={onEditGoals}
              className="text-xs text-muted-foreground hover:text-foreground transition-colors"
            >
              编辑
            </button>
          )}
        </div>
      </div>

      {/* Overall Progress */}
      <div className="mb-4">
        <div className="h-2 bg-muted rounded-full overflow-hidden">
          <motion.div
            initial={{ width: 0 }}
            animate={{ width: `${overallProgress}%` }}
            transition={{ duration: 0.5, ease: "easeOut" }}
            className="h-full bg-primary rounded-full"
          />
        </div>
        <p className="text-xs text-muted-foreground mt-1 text-right">
          整体进度 {overallProgress}%
        </p>
      </div>

      {/* Goals List */}
      <div className="space-y-3">
        {yearGoals.map((goal, index) => (
          <motion.div
            key={goal.id}
            initial={{ opacity: 0, x: -10 }}
            animate={{ opacity: 1, x: 0 }}
            transition={{ delay: index * 0.05 }}
            className={`p-3 rounded-lg ${
              goal.status === "completed"
                ? "bg-emerald-500/10"
                : "bg-muted/50"
            }`}
          >
            <div className="flex items-start gap-3">
              {/* Category Icon */}
              <span className="text-lg flex-shrink-0">
                {CATEGORY_ICONS[goal.category || "other"]}
              </span>

              {/* Content */}
              <div className="flex-1 min-w-0">
                <div className="flex items-center gap-2">
                  <h4
                    className={`text-sm font-medium truncate ${
                      goal.status === "completed"
                        ? "text-muted-foreground line-through"
                        : "text-foreground"
                    }`}
                  >
                    {goal.title}
                  </h4>
                  {goal.status === "completed" && (
                    <span className="text-xs px-1.5 py-0.5 bg-emerald-500/20 text-emerald-600 dark:text-emerald-400 rounded">
                      已完成
                    </span>
                  )}
                  {goal.status === "in_progress" && (
                    <span className="text-xs px-1.5 py-0.5 bg-blue-500/20 text-blue-600 dark:text-blue-400 rounded">
                      进行中
                    </span>
                  )}
                </div>

                {goal.description && (
                  <p className="text-xs text-muted-foreground mt-1 line-clamp-1">
                    {goal.description}
                  </p>
                )}

                {/* Progress Bar */}
                <div className="mt-2">
                  <div className="h-1.5 bg-muted rounded-full overflow-hidden">
                    <motion.div
                      initial={{ width: 0 }}
                      animate={{ width: `${goal.progress}%` }}
                      transition={{ duration: 0.5, ease: "easeOut", delay: index * 0.05 }}
                      className={`h-full rounded-full ${
                        CATEGORY_COLORS[goal.category || "other"]
                      }`}
                    />
                  </div>
                  <p className="text-[10px] text-muted-foreground mt-0.5 text-right">
                    {goal.progress}%
                  </p>
                </div>

                {/* Milestones Preview */}
                {goal.milestones && goal.milestones.length > 0 && (
                  <div className="mt-2 flex items-center gap-1">
                    {goal.milestones.slice(0, 4).map((m) => (
                      <div
                        key={m.id}
                        className={`w-2 h-2 rounded-full ${
                          m.completed ? "bg-emerald-500" : "bg-muted-foreground/30"
                        }`}
                        title={m.title}
                      />
                    ))}
                    {goal.milestones.length > 4 && (
                      <span className="text-[10px] text-muted-foreground">
                        +{goal.milestones.length - 4}
                      </span>
                    )}
                  </div>
                )}
              </div>
            </div>
          </motion.div>
        ))}
      </div>
    </motion.div>
  );
}

export default YearlyGoalsCard;

