"use client";

/**
 * YearlyGoalsCard - 年度目标卡片
 *
 * 展示年度目标列表及进度
 * 支持折叠/展开，记住用户偏好
 *
 * 基于: Journey Page 4-state 系统
 */

import { motion, AnimatePresence } from "framer-motion";
import { useState, useCallback } from "react";
import type { YearlyGoal } from "@/types/journey";

interface YearlyGoalsCardProps {
  goals: YearlyGoal[];
  /** 受控展开状态 */
  expanded?: boolean;
  /** 展开/折叠切换回调 */
  onToggle?: () => void;
  onEditGoals?: () => void;
  className?: string;
}

const CATEGORY_ICONS: Record<string, string> = {
  career: "💼",
  health: "💪",
  relationship: "❤️",
  wealth: "💰",
  growth: "🌱",
  other: "🎯",
};

const CATEGORY_COLORS: Record<string, string> = {
  career: "bg-blue-500",
  health: "bg-green-500",
  relationship: "bg-pink-500",
  wealth: "bg-amber-500",
  growth: "bg-purple-500",
  other: "bg-gray-500",
};

export function YearlyGoalsCard({
  goals,
  expanded: controlledExpanded,
  onToggle,
  onEditGoals,
  className,
}: YearlyGoalsCardProps) {
  const [localExpanded, setLocalExpanded] = useState(false);

  // 支持 controlled 和 uncontrolled 模式
  const isControlled = controlledExpanded !== undefined;
  const expanded = isControlled ? controlledExpanded : localExpanded;

  const handleToggle = useCallback(() => {
    if (isControlled && onToggle) {
      onToggle();
    } else {
      setLocalExpanded((prev) => !prev);
    }
  }, [isControlled, onToggle]);

  const currentYear = new Date().getFullYear();
  const yearGoals = goals.filter((g) => !g.year || g.year === currentYear);

  const completedCount = yearGoals.filter((g) => g.status === "completed").length;
  const totalCount = yearGoals.length;
  const overallProgress =
    totalCount > 0
      ? Math.round(yearGoals.reduce((sum, g) => sum + (g.progress || 0), 0) / totalCount)
      : 0;

  if (totalCount === 0) {
    return null;
  }

  // 折叠状态视图
  if (!expanded) {
    return (
      <motion.div
        initial={{ opacity: 0, y: 10 }}
        animate={{ opacity: 1, y: 0 }}
        className={`bg-card border border-border rounded-2xl p-3 ${className}`}
      >
        <div className="flex items-center justify-between">
          <div className="flex items-center gap-2 flex-1 min-w-0">
            <span className="text-lg">🎯</span>
            <span className="text-sm text-foreground">{currentYear} 年度目标</span>
            <span className="text-xs text-muted-foreground">
              {completedCount}/{totalCount}
            </span>
            {/* Mini progress bar */}
            <div className="flex-1 max-w-20 h-1.5 bg-muted rounded-full overflow-hidden">
              <div
                className="h-full bg-primary rounded-full transition-all"
                style={{ width: `${overallProgress}%` }}
              />
            </div>
            <span className="text-xs text-muted-foreground">{overallProgress}%</span>
          </div>
          <div className="flex items-center gap-2 flex-shrink-0">
            <button
              onClick={handleToggle}
              className="text-xs text-muted-foreground hover:text-foreground transition-colors"
            >
              展开
            </button>
            {onEditGoals && (
              <button
                onClick={onEditGoals}
                className="text-xs text-muted-foreground hover:text-foreground transition-colors"
              >
                编辑
              </button>
            )}
          </div>
        </div>
      </motion.div>
    );
  }

