"use client";

/**
 * MonthlyBossCard - 月度 Boss 卡片
 *
 * 展示本月主要项目/挑战及里程碑
 * 基于: Journey Page 4-state 系统
 */

import { motion } from "framer-motion";
import type { MonthlyBoss, Milestone } from "@/types/journey";

interface MonthlyBossCardProps {
  monthlyBoss: MonthlyBoss;
  onToggleMilestone?: (milestoneId: string) => void;
  onEditBoss?: () => void;
  className?: string;
}

const STATUS_CONFIG = {
  pending: { label: "待开始", color: "bg-gray-500/20 text-gray-600 dark:text-gray-400" },
  in_progress: { label: "进行中", color: "bg-blue-500/20 text-blue-600 dark:text-blue-400" },
  completed: { label: "已完成", color: "bg-emerald-500/20 text-emerald-600 dark:text-emerald-400" },
  failed: { label: "未达成", color: "bg-red-500/20 text-red-600 dark:text-red-400" },
};

function formatMonth(monthStr: string): string {
  try {
    const [year, month] = monthStr.split("-");
    return `${year}年${parseInt(month)}月`;
  } catch {
    return monthStr;
  }
}

export function MonthlyBossCard({
  monthlyBoss,
  onToggleMilestone,
  onEditBoss,
  className,
}: MonthlyBossCardProps) {
  const { title, description, month, progress, status, milestones = [] } = monthlyBoss;
  const statusConfig = STATUS_CONFIG[status] || STATUS_CONFIG.pending;

  const completedMilestones = milestones.filter((m) => m.completed).length;
  const totalMilestones = milestones.length;

  return (
    <motion.div
      initial={{ opacity: 0, y: 10 }}
      animate={{ opacity: 1, y: 0 }}
      className={`bg-card border border-border rounded-2xl p-4 ${className}`}
    >
      {/* Header */}
      <div className="flex items-center justify-between mb-3">
        <div className="flex items-center gap-2">
          <span className="text-xl">👹</span>
          <h3 className="font-semibold text-foreground">本月 Boss</h3>
        </div>
        <div className="flex items-center gap-2">
          <span className={`text-xs px-2 py-0.5 rounded ${statusConfig.color}`}>
            {statusConfig.label}
          </span>
          {onEditBoss && (
            <button
              onClick={onEditBoss}
              className="text-xs text-muted-foreground hover:text-foreground transition-colors"
            >
              编辑
            </button>
          )}
        </div>
      </div>

      {/* Boss Info */}
      <div className="mb-4">
        <div className="flex items-start justify-between">
          <div className="flex-1">
            <h4 className="text-base font-medium text-foreground">{title}</h4>
            {description && (
              <p className="text-sm text-muted-foreground mt-1">{description}</p>
            )}
          </div>
        </div>
        <p className="text-xs text-muted-foreground mt-2">{formatMonth(month)}</p>
      </div>

      {/* Progress */}
      <div className="mb-4">
        <div className="flex items-center justify-between text-xs text-muted-foreground mb-1">
          <span>进度</span>
          <span>{progress}%</span>
        </div>
        <div className="h-2 bg-muted rounded-full overflow-hidden">
          <motion.div
            initial={{ width: 0 }}
            animate={{ width: `${progress}%` }}
            transition={{ duration: 0.5, ease: "easeOut" }}
            className={`h-full rounded-full ${
              status === "completed"
                ? "bg-emerald-500"
                : status === "failed"
                ? "bg-red-500"
                : "bg-primary"
            }`}
          />
        </div>
      </div>

      {/* Milestones */}
      {totalMilestones > 0 && (
        <div>
          <div className="flex items-center justify-between mb-2">
            <p className="text-xs text-muted-foreground">里程碑</p>
            <p className="text-xs text-muted-foreground">
              {completedMilestones}/{totalMilestones}
            </p>
          </div>
          <div className="space-y-2">
            {milestones.map((milestone, index) => (
              <motion.div
                key={milestone.id}
                initial={{ opacity: 0, x: -10 }}
                animate={{ opacity: 1, x: 0 }}
                transition={{ delay: index * 0.05 }}
                className={`flex items-center gap-3 p-2 rounded-lg transition-colors ${
                  milestone.completed
                    ? "bg-emerald-500/10"
                    : "bg-muted/50 hover:bg-muted"
                }`}
              >
                {/* Checkbox */}
                <button
                  onClick={() => onToggleMilestone?.(milestone.id)}
                  disabled={!onToggleMilestone}
                  className={`flex-shrink-0 w-5 h-5 rounded border-2 flex items-center justify-center transition-colors ${
                    milestone.completed
                      ? "bg-emerald-500 border-emerald-500 text-white"
                      : "border-muted-foreground/30 hover:border-primary"
                  } ${!onToggleMilestone ? "cursor-default" : "cursor-pointer"}`}
                >
                  {milestone.completed && (
                    <svg
                      className="w-3 h-3"
                      fill="none"
                      viewBox="0 0 24 24"
                      stroke="currentColor"
                    >
                      <path
                        strokeLinecap="round"
                        strokeLinejoin="round"
                        strokeWidth={3}
                        d="M5 13l4 4L19 7"
                      />
                    </svg>
                  )}
                </button>

                {/* Title */}
                <span
                  className={`text-sm flex-1 ${
                    milestone.completed
                      ? "text-muted-foreground line-through"
                      : "text-foreground"
                  }`}
                >
                  {milestone.title}
                </span>
              </motion.div>
            ))}
          </div>
        </div>
      )}
    </motion.div>
  );
}

export default MonthlyBossCard;

