"use client";

/**
 * MonthlyBossCard - 月度 Boss 卡片
 *
 * 展示本月主要项目/挑战及里程碑
 * 支持折叠/展开，记住用户偏好
 *
 * 基于: Journey Page 4-state 系统
 */

import { motion } from "framer-motion";
import { useState, useCallback } from "react";
import type { MonthlyBoss, Milestone } from "@/types/journey";

interface MonthlyBossCardProps {
  monthlyBoss: MonthlyBoss;
  /** 受控展开状态 */
  expanded?: boolean;
  /** 展开/折叠切换回调 */
  onToggle?: () => void;
  onToggleMilestone?: (milestoneId: string) => void;
  onEditBoss?: () => void;
  className?: string;
}

const STATUS_CONFIG = {
  pending: { label: "待开始", color: "bg-gray-500/20 text-gray-600 dark:text-gray-400" },
  in_progress: { label: "进行中", color: "bg-blue-500/20 text-blue-600 dark:text-blue-400" },
  completed: { label: "已完成", color: "bg-emerald-500/20 text-emerald-600 dark:text-emerald-400" },
  failed: { label: "未达成", color: "bg-red-500/20 text-red-600 dark:text-red-400" },
};

function formatMonth(monthStr: string): string {
  try {
    const [year, month] = monthStr.split("-");
    return `${year}年${parseInt(month)}月`;
  } catch {
    return monthStr;
  }
}

export function MonthlyBossCard({
  monthlyBoss,
  expanded: controlledExpanded,
  onToggle,
  onToggleMilestone,
  onEditBoss,
  className,
}: MonthlyBossCardProps) {
  const [localExpanded, setLocalExpanded] = useState(true);

  // 支持 controlled 和 uncontrolled 模式
  const isControlled = controlledExpanded !== undefined;
  const expanded = isControlled ? controlledExpanded : localExpanded;

  const handleToggle = useCallback(() => {
    if (isControlled && onToggle) {
      onToggle();
    } else {
      setLocalExpanded((prev) => !prev);
    }
  }, [isControlled, onToggle]);

  const { title, description, month, progress, status, milestones = [] } = monthlyBoss;
  const statusConfig = STATUS_CONFIG[status] || STATUS_CONFIG.pending;

  const completedMilestones = milestones.filter((m) => m.completed).length;
  const totalMilestones = milestones.length;

  // 折叠状态视图
  if (!expanded) {
    return (
      <motion.div
        initial={{ opacity: 0, y: 10 }}
        animate={{ opacity: 1, y: 0 }}
        className={`bg-card border border-border rounded-2xl p-3 ${className}`}
      >
        <div className="flex items-center justify-between">
          <div className="flex items-center gap-2 flex-1 min-w-0">
            <span className="text-lg">👹</span>
            <span className="text-sm text-foreground truncate">{title}</span>
            <span className={`text-xs px-1.5 py-0.5 rounded ${statusConfig.color}`}>
              {statusConfig.label}
            </span>
            {/* Mini progress bar */}
            <div className="flex-1 max-w-16 h-1.5 bg-muted rounded-full overflow-hidden">
              <div
                className={`h-full rounded-full transition-all ${
                  status === "completed" ? "bg-emerald-500" : "bg-primary"
                }`}
                style={{ width: `${progress}%` }}
              />
            </div>
            <span className="text-xs text-muted-foreground">{progress}%</span>
          </div>
          <div className="flex items-center gap-2 flex-shrink-0">
            <button
              onClick={handleToggle}
