  /** 受控展开状态 */
  expanded?: boolean;
  /** 展开/折叠切换回调 */
  onToggle?: () => void;
  onToggleAction?: (actionId: string) => void;
  onAddAction?: () => void;
  className?: string;
}

export function WeeklyActionsCard({
  weekly,
  expanded: controlledExpanded,
  onToggle,
  onToggleAction,
  onAddAction,
  className,
}: WeeklyActionsCardProps) {
  const [localExpanded, setLocalExpanded] = useState(true);

  // 支持 controlled 和 uncontrolled 模式
  const isControlled = controlledExpanded !== undefined;
  const expanded = isControlled ? controlledExpanded : localExpanded;

  const handleToggleFold = useCallback(() => {
    if (isControlled && onToggle) {
      onToggle();
    } else {
      setLocalExpanded((prev) => !prev);
    }
  }, [isControlled, onToggle]);

  const rocks = weekly.rocks || [];
  const completedCount = rocks.filter((r) => r.status === "completed").length;
  const totalCount = rocks.length;
  const completionRate = totalCount > 0 ? Math.round((completedCount / totalCount) * 100) : 0;

  const handleToggle = useCallback(
    (action: WeeklyAction) => {
      const id = action.id || action.task;
      onToggleAction?.(id);
    },
    [onToggleAction]
  );

  // 折叠状态视图
  if (!expanded && totalCount > 0) {
    return (
      <motion.div
        initial={{ opacity: 0, y: 10 }}
        animate={{ opacity: 1, y: 0 }}
        className={`bg-card border border-border rounded-2xl p-3 ${className}`}
      >
        <div className="flex items-center justify-between">
          <div className="flex items-center gap-2 flex-1 min-w-0">
            <span className="text-lg">🪨</span>
            <span className="text-sm text-foreground">本周行动</span>
            <span className="text-xs text-muted-foreground">
              {completedCount}/{totalCount}
            </span>
            {/* Mini progress bar */}
            <div className="flex-1 max-w-20 h-1.5 bg-muted rounded-full overflow-hidden">
              <div
                className="h-full bg-emerald-500 rounded-full transition-all"
                style={{ width: `${completionRate}%` }}
              />
            </div>
            <span className="text-xs text-muted-foreground">{completionRate}%</span>
          </div>
          <div className="flex items-center gap-2 flex-shrink-0">
            <button
              onClick={handleToggleFold}
              className="text-xs text-muted-foreground hover:text-foreground transition-colors"
            >
              展开
            </button>
          </div>
        </div>
      </motion.div>
    );
  }

  if (totalCount === 0) {
    return (
      <motion.div
        initial={{ opacity: 0, y: 10 }}
        animate={{ opacity: 1, y: 0 }}
        className={`bg-card border border-border rounded-2xl p-4 ${className}`}
      >
        <div className="flex items-center gap-2 mb-3">
          <span className="text-xl">🪨</span>
          <h3 className="font-semibold text-foreground">本周行动</h3>
        </div>
        <div className="text-center py-6">
          <p className="text-sm text-muted-foreground mb-3">
            还没有设定本周行动
          </p>
          {onAddAction && (
            <button
              onClick={onAddAction}
              className="text-sm text-primary hover:underline"
            >
              + 添加行动
            </button>
          )}
        </div>
      </motion.div>
    );
  }

  return (
    <motion.div
      initial={{ opacity: 0, y: 10 }}
      animate={{ opacity: 1, y: 0 }}
      className={`bg-card border border-border rounded-2xl p-4 ${className}`}
    >
      {/* Header */}
      <div className="flex items-center justify-between mb-3">
        <div className="flex items-center gap-2">
          <span className="text-xl">🪨</span>
          <h3 className="font-semibold text-foreground">本周行动</h3>
        </div>
        <span className="text-sm text-muted-foreground">
          {completedCount}/{totalCount}
        </span>
      </div>

      {/* Week Info */}
      {weekly.week_start && (
        <p className="text-xs text-muted-foreground mb-3">
          {weekly.week_start} 开始
          {weekly.theme && ` · ${weekly.theme}`}
        </p>
      )}

      {/* Actions List */}
      <div className="space-y-2">
        {rocks.map((action, index) => (
          <motion.div
            key={action.id || index}
            initial={{ opacity: 0, x: -10 }}
            animate={{ opacity: 1, x: 0 }}
            transition={{ delay: index * 0.05 }}
            className={`flex items-start gap-3 p-3 rounded-lg transition-colors ${
              action.status === "completed"
                ? "bg-emerald-500/10"
                : "bg-muted/50 hover:bg-muted"
            }`}
          >
            {/* Checkbox */}
            <button
              onClick={() => handleToggle(action)}
              className={`flex-shrink-0 w-5 h-5 rounded border-2 flex items-center justify-center transition-colors ${
                action.status === "completed"
                  ? "bg-emerald-500 border-emerald-500 text-white"
                  : "border-muted-foreground/30 hover:border-primary"
              }`}
            >
              {action.status === "completed" && (
                <svg
                  className="w-3 h-3"
                  fill="none"
                  viewBox="0 0 24 24"
                  stroke="currentColor"
                >
                  <path
                    strokeLinecap="round"
                    strokeLinejoin="round"
                    strokeWidth={3}
                    d="M5 13l4 4L19 7"
                  />
                </svg>
              )}
            </button>

            {/* Task Text */}
            <span
              className={`text-sm flex-1 ${
                action.status === "completed"
                  ? "text-muted-foreground line-through"
                  : "text-foreground"
              }`}
            >
              {action.task}
            </span>

            {/* Status Badge */}
            {action.status === "in_progress" && (
              <span className="text-xs px-2 py-0.5 bg-blue-500/20 text-blue-600 dark:text-blue-400 rounded-full">
                进行中
              </span>
            )}
          </motion.div>
        ))}
      </div>

      {/* Add Action Button */}
      {onAddAction && (
        <button
          onClick={onAddAction}
          className="w-full mt-3 py-2 text-sm text-muted-foreground hover:text-foreground hover:bg-muted rounded-lg transition-colors"
        >
          + 添加行动
        </button>
      )}

      {/* Progress Bar */}
      {totalCount > 0 && (
        <div className="mt-4">
          <div className="h-1.5 bg-muted rounded-full overflow-hidden">
            <motion.div
              initial={{ width: 0 }}
              animate={{ width: `${(completedCount / totalCount) * 100}%` }}
              transition={{ duration: 0.5, ease: "easeOut" }}
              className="h-full bg-emerald-500 rounded-full"
            />
          </div>
          <p className="text-xs text-muted-foreground mt-1 text-right">
            {Math.round((completedCount / totalCount) * 100)}% 完成
          </p>
        </div>
      )}
    </motion.div>
  );
}

export default WeeklyActionsCard;

