import { NextRequest, NextResponse } from "next/server";
import { API_INTERNAL } from "@/lib/backend-config";

/**
 * Journey API Route - ä»£ç†åˆ°åç«¯ lifecoach state API
 *
 * GET: è¯»å– lifecoach skill_data
 * POST: å†™å…¥ lifecoach skill_data
 */

export async function GET(request: NextRequest) {
  const auth = request.headers.get("Authorization");

  if (!auth) {
    return NextResponse.json({ error: "Unauthorized" }, { status: 401 });
  }

  try {
    // è°ƒç”¨åç«¯ skill service
    const response = await fetch(`${API_INTERNAL}/api/v1/skills/lifecoach/state_read`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Authorization: auth,
      },
      body: JSON.stringify({
        args: {
          sections: ["system", "north_star", "identity", "current", "progress"],
        },
      }),
    });

    const data = await response.json();
    return NextResponse.json(data, { status: response.status });
  } catch (error) {
    console.error("Journey API error:", error);
    return NextResponse.json(
      { status: "error", message: "Failed to fetch journey data" },
      { status: 500 }
    );
  }
}

export async function POST(request: NextRequest) {
  const auth = request.headers.get("Authorization");

  if (!auth) {
    return NextResponse.json({ error: "Unauthorized" }, { status: 401 });
  }

  try {
    const body = await request.json();

    // è°ƒç”¨åç«¯ skill service
    const response = await fetch(`${API_INTERNAL}/api/v1/skills/lifecoach/state_write`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Authorization: auth,
      },
      body: JSON.stringify({
        args: body.args || body,
      }),
    });

    const data = await response.json();
    return NextResponse.json(data, { status: response.status });
  } catch (error) {
    console.error("Journey API error:", error);
    return NextResponse.json(
      { status: "error", message: "Failed to update journey data" },
      { status: 500 }
    );
  }
}

