"use client";

import { cn } from "@/lib/utils";
import { type ReactNode } from "react";

// ═══════════════════════════════════════════════════════════════════════════
// LuminousPaper - LUMINOUS PAPER Design System Core Component
// 光纸背景：Vellum 底色 + 五行色 glow
// ═══════════════════════════════════════════════════════════════════════════

export type SkillType = "bazi" | "zodiac" | "mbti" | "attach" | "career";

interface LuminousPaperProps {
  children: ReactNode;
  skill?: SkillType;
  variant?: "default" | "subtle" | "hero";
  className?: string;
  showGrain?: boolean;
}

export function LuminousPaper({
  children,
  skill = "bazi",
  variant = "default",
  className,
  showGrain = true,
}: LuminousPaperProps) {
  return (
    <div
      data-skill={skill}
      className={cn(
        "relative min-h-screen",
        variant === "default" && "luminous-paper",
        variant === "subtle" && "vellum-bg",
        variant === "hero" && "luminous-paper",
        className
      )}
    >
      {/* Skill-specific glow overlay */}
      {variant === "hero" && (
        <div
          className="absolute inset-0 pointer-events-none"
          style={{
            background: `radial-gradient(ellipse 100% 60% at 50% -10%, var(--skill-glow), transparent 60%)`,
          }}
        />
      )}

      {/* Grain texture overlay */}
      {showGrain && (
        <div
          className="absolute inset-0 pointer-events-none opacity-[0.03]"
          style={{
            backgroundImage: `url("data:image/svg+xml,%3Csvg viewBox='0 0 400 400' xmlns='http://www.w3.org/2000/svg'%3E%3Cfilter id='noiseFilter'%3E%3CfeTurbulence type='fractalNoise' baseFrequency='0.9' numOctaves='4' stitchTiles='stitch'/%3E%3C/filter%3E%3Crect width='100%25' height='100%25' filter='url(%23noiseFilter)'/%3E%3C/svg%3E")`,
          }}
        />
      )}

      {/* Content */}
      <div className="relative z-10">{children}</div>
    </div>
  );
}

// ═══════════════════════════════════════════════════════════════════════════
// LuminousCard - Glass card with skill-aware styling
// ═══════════════════════════════════════════════════════════════════════════

interface LuminousCardProps {
  children: ReactNode;
  variant?: "glass" | "solid" | "outline";
  className?: string;
}

export function LuminousCard({
  children,
  variant = "glass",
  className,
}: LuminousCardProps) {
  return (
    <div
      className={cn(
        "rounded-xl transition-all duration-200",
        variant === "glass" && "glass-card",
        variant === "solid" && "bg-card border border-border shadow-card",
        variant === "outline" && "border border-border bg-transparent",
        className
      )}
    >
      {children}
    </div>
  );
}

export default LuminousPaper;

"use client";

import { cn } from "@/lib/utils";
import { type SkillType } from "./LuminousPaper";

// ═══════════════════════════════════════════════════════════════════════════
// BreathAura - LUMINOUS PAPER Design System
// 呼吸光晕：根据五行属性变色的动态光晕
// ═══════════════════════════════════════════════════════════════════════════

interface BreathAuraProps {
  size?: "sm" | "md" | "lg" | "xl";
  skill?: SkillType;
  intensity?: "low" | "medium" | "high";
  position?: "center" | "top" | "bottom" | "left" | "right";
  className?: string;
  animate?: boolean;
}

const sizeClasses = {
  sm: "w-32 h-32",
  md: "w-48 h-48",
  lg: "w-64 h-64",
  xl: "w-96 h-96",
};

const positionClasses = {
  center: "top-1/2 left-1/2 -translate-x-1/2 -translate-y-1/2",
  top: "top-0 left-1/2 -translate-x-1/2 -translate-y-1/2",
  bottom: "bottom-0 left-1/2 -translate-x-1/2 translate-y-1/2",
  left: "top-1/2 left-0 -translate-x-1/2 -translate-y-1/2",
  right: "top-1/2 right-0 translate-x-1/2 -translate-y-1/2",
};

const skillGlowColors: Record<SkillType, string> = {
  bazi: "rgba(122, 63, 26, 0.25)",
  zodiac: "rgba(60, 58, 122, 0.2)",
  mbti: "rgba(15, 118, 110, 0.2)",
  attach: "rgba(177, 60, 65, 0.2)",
  career: "rgba(14, 116, 144, 0.2)",
};

const intensityOpacity = {
  low: 0.5,
  medium: 0.75,
  high: 1,
};

export function BreathAura({
  size = "md",
  skill = "bazi",
  intensity = "medium",
  position = "center",
  className,
  animate = true,
}: BreathAuraProps) {
  return (
    <div
      className={cn(
        "absolute rounded-full blur-3xl pointer-events-none",
        sizeClasses[size],
        positionClasses[position],
        animate && "animate-breath",
        className
      )}
      style={{
        background: skillGlowColors[skill],
        opacity: intensityOpacity[intensity],
      }}
    />
  );
}

// ═══════════════════════════════════════════════════════════════════════════
// MultiBreathAura - Multiple layered auras for richer effect
// ═══════════════════════════════════════════════════════════════════════════

interface MultiBreathAuraProps {
  skill?: SkillType;
  className?: string;
}

export function MultiBreathAura({ skill = "bazi", className }: MultiBreathAuraProps) {
  return (
    <div className={cn("absolute inset-0 overflow-hidden pointer-events-none", className)}>
      <BreathAura
        skill={skill}
        size="xl"
        position="top"
        intensity="low"
        className="opacity-60"
      />
      <BreathAura
        skill={skill}
        size="lg"
        position="center"
        intensity="medium"
        className="opacity-40 animation-delay-1000"
      />
      <BreathAura
        skill={skill}
        size="md"
        position="bottom"
        intensity="low"
        className="opacity-30 animation-delay-2000"
      />
    </div>
  );
}

export default BreathAura;

"use client";

import { cn } from "@/lib/utils";
import { type SkillType } from "./LuminousPaper";
import { BreathAura } from "./BreathAura";

// ═══════════════════════════════════════════════════════════════════════════
// VibeGlyph - LUMINOUS PAPER Design System
// 品牌图标：五行循环图形 + 呼吸光晕
// ═══════════════════════════════════════════════════════════════════════════

interface VibeGlyphProps {
  size?: "sm" | "md" | "lg" | "xl";
  skill?: SkillType;
  showAura?: boolean;
  animate?: boolean;
  className?: string;
}

const sizeConfig = {
  sm: { container: "w-16 h-16", icon: 24, aura: "sm" as const },
  md: { container: "w-24 h-24", icon: 36, aura: "md" as const },
  lg: { container: "w-32 h-32", icon: 48, aura: "lg" as const },
  xl: { container: "w-48 h-48", icon: 72, aura: "xl" as const },
};

const skillColors: Record<SkillType, { primary: string; secondary: string }> = {
  bazi: { primary: "#7A3F1A", secondary: "#B88A44" },
  zodiac: { primary: "#3C3A7A", secondary: "#A7A6F4" },
  mbti: { primary: "#0F766E", secondary: "#6EE7C3" },
  attach: { primary: "#B13C41", secondary: "#F3A6A9" },
  career: { primary: "#0E7490", secondary: "#67E8F9" },
};

// Five Elements Glyph SVG
function FiveElementsIcon({ size, skill }: { size: number; skill: SkillType }) {
  const colors = skillColors[skill];

  return (
    <svg
      width={size}
      height={size}
      viewBox="0 0 48 48"
      fill="none"
      xmlns="http://www.w3.org/2000/svg"
    >
      {/* Outer ring */}
      <circle
        cx="24"
        cy="24"
        r="20"
        stroke={colors.secondary}
        strokeWidth="1.5"
        strokeOpacity="0.3"
      />

      {/* Five elements points - pentagon arrangement */}
