}: PersonalSealProps) {
  const pattern = generateSealPattern(birthYear, birthMonth, birthDay);

  return (
    <InsightSeal
      skill={skill}
      size={size}
      label={pattern}
      className={className}
    />
  );
}

// ═══════════════════════════════════════════════════════════════════════════
// SealBadge - Inline badge with seal styling
// ═══════════════════════════════════════════════════════════════════════════

interface SealBadgeProps {
  children: React.ReactNode;
  type?: InsightType;
  className?: string;
}

const badgeColors: Record<InsightType, string> = {
  discovery: "bg-element-water/10 text-element-water border-element-water/20",
  pattern: "bg-zodiac-primary/10 text-zodiac-primary border-zodiac-primary/20",
  timing: "bg-antique-gold/10 text-antique-gold border-antique-gold/20",
  growth: "bg-element-wood/10 text-element-wood border-element-wood/20",
};

export function SealBadge({
  children,
  type = "discovery",
  className,
}: SealBadgeProps) {
  return (
    <span
      className={cn(
        "inline-flex items-center gap-1 px-2 py-0.5",
        "text-xs font-medium rounded-full border",
        badgeColors[type],
        className
      )}
    >
      {children}
    </span>
  );
}

export default InsightSeal;

// ═══════════════════════════════════════════════════════════════════════════
// LUMINOUS PAPER Design System - Core Components
// ═══════════════════════════════════════════════════════════════════════════

export { LuminousPaper, LuminousCard, type SkillType } from "./LuminousPaper";
export { BreathAura, MultiBreathAura } from "./BreathAura";
export { VibeGlyph, SimpleVibeGlyph } from "./VibeGlyph";
export { InsightSeal, PersonalSeal, SealBadge, type InsightType } from "./InsightSeal";

