"use client";

import { ButtonHTMLAttributes, forwardRef } from "react";
import { cn } from "@/lib/utils";

// ═══════════════════════════════════════════════════════════════════════════
// Button - LUMINOUS PAPER Design System
// 墨色 CTA 风格：Primary 用 Ink，Gold 只做点缀
// ═══════════════════════════════════════════════════════════════════════════

export interface ButtonProps extends ButtonHTMLAttributes<HTMLButtonElement> {
  variant?: "primary" | "secondary" | "outline" | "ghost" | "gold" | "skill";
  size?: "sm" | "md" | "lg";
  isLoading?: boolean;
  rounded?: "default" | "full";
}

export const Button = forwardRef<HTMLButtonElement, ButtonProps>(
  (
    {
      className,
      variant = "primary",
      size = "md",
      rounded = "full",
      isLoading,
      children,
      disabled,
      ...props
    },
    ref
  ) => {
    const baseStyles = cn(
      "inline-flex items-center justify-center gap-2",
      "font-sans font-medium",
      "transition-all duration-200 ease-out-expo",
      "focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring focus-visible:ring-offset-2",
      "disabled:pointer-events-none disabled:opacity-50",
      "active:scale-[0.98]"
    );

    const variants = {
      // Primary: Ink (墨色) - 主要 CTA
      primary: cn(
        "bg-ink text-vellum",
        "hover:bg-ink/90",
        "shadow-sm hover:shadow-md"
      ),
      // Secondary: Card background with border
      secondary: cn(
        "bg-card text-foreground",
        "border border-border",
        "hover:bg-muted"
      ),
      // Outline: Transparent with border
      outline: cn(
        "bg-transparent text-foreground",
        "border border-border",
        "hover:bg-muted/50"
      ),
      // Ghost: No background
      ghost: cn(
        "bg-transparent text-foreground",
        "hover:bg-muted/50"
      ),
      // Gold: Antique Gold accent (少量使用)
      gold: cn(
        "bg-antique-gold text-white",
        "hover:bg-antique-gold/90",
        "shadow-glow-gold hover:shadow-glow-gold"
      ),
      // Skill: Uses CSS variable colors
      skill: cn(
        "bg-skill-primary text-white",
        "hover:opacity-90"
      ),
    };

    const sizes = {
      sm: "px-4 py-2 text-sm",
      md: "px-6 py-3 text-base",
      lg: "px-8 py-4 text-lg",
    };

    const roundedStyles = {
      default: "rounded-lg",
      full: "rounded-full",
    };

    return (
      <button
        ref={ref}
        className={cn(
          baseStyles,
          variants[variant],
          sizes[size],
          roundedStyles[rounded],
          className
        )}
        disabled={disabled || isLoading}
        {...props}
      >
        {isLoading && (
          <svg
            className="animate-spin h-4 w-4"
            fill="none"
            viewBox="0 0 24 24"
          >
            <circle
              className="opacity-25"
              cx="12"
              cy="12"
              r="10"
              stroke="currentColor"
              strokeWidth="4"
            />
            <path
              className="opacity-75"
              fill="currentColor"
              d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4z"
            />
          </svg>
        )}
        {children}
      </button>
    );
  }
);

Button.displayName = "Button";

// ═══════════════════════════════════════════════════════════════════════════
// IconButton - Square button for icons
// ═══════════════════════════════════════════════════════════════════════════

export interface IconButtonProps extends ButtonHTMLAttributes<HTMLButtonElement> {
  variant?: "primary" | "secondary" | "ghost";
  size?: "sm" | "md" | "lg";
}

export const IconButton = forwardRef<HTMLButtonElement, IconButtonProps>(
  ({ className, variant = "ghost", size = "md", children, ...props }, ref) => {
    const variants = {
      primary: "bg-ink text-vellum hover:bg-ink/90",
      secondary: "bg-card text-foreground border border-border hover:bg-muted",
      ghost: "text-muted-foreground hover:text-foreground hover:bg-muted/50",
    };

    const sizes = {
      sm: "w-8 h-8",
      md: "w-10 h-10",
      lg: "w-12 h-12",
    };

    return (
      <button
        ref={ref}
        className={cn(
          "inline-flex items-center justify-center rounded-full",
          "transition-all duration-200",
          "focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring",
          "disabled:pointer-events-none disabled:opacity-50",
          "active:scale-95",
          variants[variant],
          sizes[size],
          className
        )}
        {...props}
      >
        {children}
      </button>
    );
  }
);

IconButton.displayName = "IconButton";

"use client";

import { HTMLAttributes, forwardRef } from "react";
import { cn } from "@/lib/utils";

// ═══════════════════════════════════════════════════════════════════════════
// Card - LUMINOUS PAPER Design System
// glass-card 玻璃态效果
// ═══════════════════════════════════════════════════════════════════════════

export interface CardProps extends HTMLAttributes<HTMLDivElement> {
  variant?: "default" | "glass" | "bordered" | "insight";
  hover?: boolean;
}

export const Card = forwardRef<HTMLDivElement, CardProps>(
  ({ className, variant = "default", hover = true, children, ...props }, ref) => {
    const variants = {
      // Default: Solid card with subtle shadow
      default: cn(
        "bg-card text-card-foreground",
        "border border-border",
        "shadow-card",
        hover && "hover:shadow-card-hover"
      ),
      // Glass: Frosted glass effect
      glass: cn(
        "glass-card",
        hover && "hover:shadow-glass-hover"
      ),
      // Bordered: Outline only
      bordered: cn(
        "bg-transparent",
        "border border-border",
        hover && "hover:bg-muted/30"
      ),
      // Insight: For insight cards with left border
      insight: cn(
        "bg-card text-card-foreground",
        "border border-border border-l-4",
        "shadow-insight",
        "border-l-skill-primary"
      ),
    };

    return (
      <div
        ref={ref}
        className={cn(
          "rounded-xl transition-all duration-200",
          variants[variant],
          className
        )}
        {...props}
      >
        {children}
      </div>
    );
  }
);

Card.displayName = "Card";

// ═══════════════════════════════════════════════════════════════════════════
// Card Sub-components
// ═══════════════════════════════════════════════════════════════════════════

export const CardHeader = forwardRef<HTMLDivElement, HTMLAttributes<HTMLDivElement>>(
  ({ className, ...props }, ref) => (
    <div
      ref={ref}
      className={cn("p-6 pb-0", className)}
      {...props}
    />
  )
);
CardHeader.displayName = "CardHeader";

export const CardTitle = forwardRef<HTMLHeadingElement, HTMLAttributes<HTMLHeadingElement>>(
  ({ className, ...props }, ref) => (
    <h3
      ref={ref}
      className={cn(
        "font-serif text-card-title font-semibold tracking-tight text-foreground",
        className
      )}
      {...props}
    />
  )
);
CardTitle.displayName = "CardTitle";

export const CardDescription = forwardRef<HTMLParagraphElement, HTMLAttributes<HTMLParagraphElement>>(
  ({ className, ...props }, ref) => (
    <p
      ref={ref}
      className={cn("text-sm text-muted-foreground mt-1", className)}
      {...props}
    />
  )
);
CardDescription.displayName = "CardDescription";

export const CardContent = forwardRef<HTMLDivElement, HTMLAttributes<HTMLDivElement>>(
  ({ className, ...props }, ref) => (
    <div
      ref={ref}
      className={cn("p-6", className)}
      {...props}
    />
  )
);
CardContent.displayName = "CardContent";

export const CardFooter = forwardRef<HTMLDivElement, HTMLAttributes<HTMLDivElement>>(
  ({ className, ...props }, ref) => (
    <div
      ref={ref}
      className={cn("p-6 pt-0 flex items-center gap-3", className)}
      {...props}
    />
  )
);
CardFooter.displayName = "CardFooter";

// ═══════════════════════════════════════════════════════════════════════════
// GlassCard - Convenience component for glass variant
// ═══════════════════════════════════════════════════════════════════════════

export interface GlassCardProps extends HTMLAttributes<HTMLDivElement> {
  hover?: boolean;
}

export const GlassCard = forwardRef<HTMLDivElement, GlassCardProps>(
  ({ className, children, ...props }, ref) => (
    <Card ref={ref} variant="glass" className={className} {...props}>
      {children}
    </Card>
  )
);
GlassCard.displayName = "GlassCard";

"use client";

import { InputHTMLAttributes, SelectHTMLAttributes, TextareaHTMLAttributes, forwardRef } from "react";
import { cn } from "@/lib/utils";

// ═══════════════════════════════════════════════════════════════════════════
// Input - LUMINOUS PAPER Design System
// Vibe Capsule 输入样式
// ═══════════════════════════════════════════════════════════════════════════

export interface InputProps extends InputHTMLAttributes<HTMLInputElement> {
  label?: string;
  error?: string;
  variant?: "default" | "capsule";
}

export const Input = forwardRef<HTMLInputElement, InputProps>(
  ({ className, label, error, type = "text", variant = "default", ...props }, ref) => {
    return (
      <div className="w-full">
        {label && (
          <label className="label">{label}</label>
        )}
        <input
          ref={ref}
          type={type}
          className={cn(
            variant === "default" && "input",
            variant === "capsule" && "input-capsule",
            error && "border-red-500 focus:ring-red-500",
            className
          )}
          {...props}
        />
        {error && (
          <p className="mt-1.5 text-sm text-red-500">{error}</p>
        )}
      </div>
    );
  }
);

Input.displayName = "Input";

// ═══════════════════════════════════════════════════════════════════════════
// Select - Dropdown select with custom styling
// ═══════════════════════════════════════════════════════════════════════════

export interface SelectProps extends SelectHTMLAttributes<HTMLSelectElement> {
  label?: string;
  error?: string;
  options: Array<{ value: string; label: string }>;
  placeholder?: string;
}

export const Select = forwardRef<HTMLSelectElement, SelectProps>(
  ({ className, label, error, options, placeholder, ...props }, ref) => {
    return (
      <div className="w-full">
        {label && (
          <label className="label">{label}</label>
        )}
        <select
          ref={ref}
          className={cn(
            "select",
            error && "border-red-500 focus:ring-red-500",
            className
          )}
          {...props}
        >
          {placeholder && (
            <option value="" disabled>
              {placeholder}
            </option>
          )}
          {options.map((option) => (
            <option key={option.value} value={option.value}>
              {option.label}
            </option>
          ))}
        </select>
        {error && (
          <p className="mt-1.5 text-sm text-red-500">{error}</p>
        )}
      </div>
    );
  }
);

Select.displayName = "Select";

// ═══════════════════════════════════════════════════════════════════════════
// Textarea - Multi-line input
// ═══════════════════════════════════════════════════════════════════════════

export interface TextareaProps extends TextareaHTMLAttributes<HTMLTextAreaElement> {
  label?: string;
  error?: string;
}

export const Textarea = forwardRef<HTMLTextAreaElement, TextareaProps>(
  ({ className, label, error, ...props }, ref) => {
    return (
      <div className="w-full">
        {label && (
          <label className="label">{label}</label>
        )}
        <textarea
          ref={ref}
          className={cn(
            "input min-h-[100px] resize-y",
            error && "border-red-500 focus:ring-red-500",
            className
          )}
          {...props}
        />
        {error && (
          <p className="mt-1.5 text-sm text-red-500">{error}</p>
        )}
      </div>
    );
  }
);

Textarea.displayName = "Textarea";

// ═══════════════════════════════════════════════════════════════════════════
// BirthDateInputs - Special inputs for birth date selection
// ═══════════════════════════════════════════════════════════════════════════

interface BirthDateInputsProps {
  year: string;
  month: string;
  day: string;
  hour?: string;
  onYearChange: (value: string) => void;
  onMonthChange: (value: string) => void;
  onDayChange: (value: string) => void;
  onHourChange?: (value: string) => void;
  showHour?: boolean;
  className?: string;
}

const years = Array.from({ length: 100 }, (_, i) => ({
  value: String(2024 - i),
  label: `${2024 - i}年`,
}));

const months = Array.from({ length: 12 }, (_, i) => ({
  value: String(i + 1).padStart(2, "0"),
  label: `${i + 1}月`,
}));

const days = Array.from({ length: 31 }, (_, i) => ({
  value: String(i + 1).padStart(2, "0"),
  label: `${i + 1}日`,
}));

const hours = [
  { value: "unknown", label: "不确定" },
  { value: "23-01", label: "子时 (23:00-01:00)" },
  { value: "01-03", label: "丑时 (01:00-03:00)" },
  { value: "03-05", label: "寅时 (03:00-05:00)" },
  { value: "05-07", label: "卯时 (05:00-07:00)" },
  { value: "07-09", label: "辰时 (07:00-09:00)" },
  { value: "09-11", label: "巳时 (09:00-11:00)" },
  { value: "11-13", label: "午时 (11:00-13:00)" },
  { value: "13-15", label: "未时 (13:00-15:00)" },
  { value: "15-17", label: "申时 (15:00-17:00)" },
  { value: "17-19", label: "酉时 (17:00-19:00)" },
  { value: "19-21", label: "戌时 (19:00-21:00)" },
  { value: "21-23", label: "亥时 (21:00-23:00)" },
];

export function BirthDateInputs({
  year,
  month,
  day,
  hour,
  onYearChange,
  onMonthChange,
  onDayChange,
  onHourChange,
  showHour = true,
  className,
}: BirthDateInputsProps) {
  return (
    <div className={cn("grid gap-3", showHour ? "grid-cols-4" : "grid-cols-3", className)}>
      <Select
        value={year}
        onChange={(e) => onYearChange(e.target.value)}
        options={years}
        placeholder="年份"
      />
      <Select
        value={month}
        onChange={(e) => onMonthChange(e.target.value)}
        options={months}
        placeholder="月份"
      />
      <Select
        value={day}
        onChange={(e) => onDayChange(e.target.value)}
        options={days}
        placeholder="日期"
      />
      {showHour && onHourChange && (
        <Select
          value={hour || "unknown"}
          onChange={(e) => onHourChange(e.target.value)}
          options={hours}
          placeholder="时辰"
        />
      )}
    </div>
  );
}

