"use client";

import { HTMLAttributes, forwardRef } from "react";
import { cn } from "@/lib/utils";

// ═══════════════════════════════════════════════════════════════════════════
// Card - LUMINOUS PAPER Design System
// glass-card 玻璃态效果
// ═══════════════════════════════════════════════════════════════════════════

export interface CardProps extends HTMLAttributes<HTMLDivElement> {
  variant?: "default" | "glass" | "bordered" | "insight";
  hover?: boolean;
}

export const Card = forwardRef<HTMLDivElement, CardProps>(
  ({ className, variant = "default", hover = true, children, ...props }, ref) => {
    const variants = {
      // Default: Solid card with subtle shadow
      default: cn(
        "bg-card text-card-foreground",
        "border border-border",
        "shadow-card",
        hover && "hover:shadow-card-hover"
      ),
      // Glass: Frosted glass effect
      glass: cn(
        "glass-card",
        hover && "hover:shadow-glass-hover"
      ),
      // Bordered: Outline only
      bordered: cn(
        "bg-transparent",
        "border border-border",
        hover && "hover:bg-muted/30"
      ),
      // Insight: For insight cards with left border
      insight: cn(
        "bg-card text-card-foreground",
        "border border-border border-l-4",
        "shadow-insight",
        "border-l-skill-primary"
      ),
    };

    return (
      <div
        ref={ref}
        className={cn(
          "rounded-xl transition-all duration-200",
          variants[variant],
          className
        )}
        {...props}
      >
        {children}
      </div>
    );
  }
);

Card.displayName = "Card";

// ═══════════════════════════════════════════════════════════════════════════
// Card Sub-components
// ═══════════════════════════════════════════════════════════════════════════

export const CardHeader = forwardRef<HTMLDivElement, HTMLAttributes<HTMLDivElement>>(
  ({ className, ...props }, ref) => (
    <div
      ref={ref}
      className={cn("p-6 pb-0", className)}
      {...props}
    />
  )
);
CardHeader.displayName = "CardHeader";

export const CardTitle = forwardRef<HTMLHeadingElement, HTMLAttributes<HTMLHeadingElement>>(
  ({ className, ...props }, ref) => (
    <h3
      ref={ref}
      className={cn(
        "font-serif text-card-title font-semibold tracking-tight text-foreground",
        className
      )}
      {...props}
    />
  )
);
CardTitle.displayName = "CardTitle";

export const CardDescription = forwardRef<HTMLParagraphElement, HTMLAttributes<HTMLParagraphElement>>(
  ({ className, ...props }, ref) => (
    <p
      ref={ref}
      className={cn("text-sm text-muted-foreground mt-1", className)}
      {...props}
    />
  )
);
CardDescription.displayName = "CardDescription";

export const CardContent = forwardRef<HTMLDivElement, HTMLAttributes<HTMLDivElement>>(
  ({ className, ...props }, ref) => (
    <div
      ref={ref}
      className={cn("p-6", className)}
      {...props}
    />
  )
);
CardContent.displayName = "CardContent";

export const CardFooter = forwardRef<HTMLDivElement, HTMLAttributes<HTMLDivElement>>(
  ({ className, ...props }, ref) => (
    <div
      ref={ref}
      className={cn("p-6 pt-0 flex items-center gap-3", className)}
      {...props}
    />
  )
);
CardFooter.displayName = "CardFooter";

// ═══════════════════════════════════════════════════════════════════════════
// GlassCard - Convenience component for glass variant
// ═══════════════════════════════════════════════════════════════════════════

export interface GlassCardProps extends HTMLAttributes<HTMLDivElement> {
  hover?: boolean;
}

export const GlassCard = forwardRef<HTMLDivElement, GlassCardProps>(
  ({ className, children, ...props }, ref) => (
    <Card ref={ref} variant="glass" className={className} {...props}>
      {children}
    </Card>
  )
);
GlassCard.displayName = "GlassCard";

