"use client";

import { motion } from "framer-motion";
import { cn } from "@/lib/utils";
import { Bookmark, Share2, Sparkles, RefreshCw, Clock, Sprout } from "lucide-react";
import { InsightSeal, SealBadge, type InsightType } from "@/components/core";

// ═══════════════════════════════════════════════════════════════════════════
// InsightCard - LUMINOUS PAPER Design System
// 洞察卡片：带 Insight Seal 印章系统
// ═══════════════════════════════════════════════════════════════════════════

export type { InsightType };

export interface InsightCardProps {
  type: InsightType;
  title: string;
  content: string;
  onSave?: () => void;
  onShare?: () => void;
  onDeepDive?: () => void;
  showSeal?: boolean;
  className?: string;
}

const INSIGHT_CONFIG: Record<InsightType, {
  icon: typeof Sparkles;
  defaultTitle: string;
  borderColor: string;
  glowColor: string;
}> = {
  discovery: {
    icon: Sparkles,
    defaultTitle: "我看见你...",
    borderColor: "border-l-element-water",
    glowColor: "shadow-[0_2px_16px_rgba(58,129,180,0.15)]",
  },
  pattern: {
    icon: RefreshCw,
    defaultTitle: "我注意到一个模式...",
    borderColor: "border-l-zodiac-primary",
    glowColor: "shadow-[0_2px_16px_rgba(60,58,122,0.15)]",
  },
  timing: {
    icon: Clock,
    defaultTitle: "现在是...的好时机",
    borderColor: "border-l-antique-gold",
    glowColor: "shadow-[0_2px_16px_rgba(184,138,68,0.15)]",
  },
  growth: {
    icon: Sprout,
    defaultTitle: "你的成长...",
    borderColor: "border-l-element-wood",
    glowColor: "shadow-[0_2px_16px_rgba(51,128,64,0.15)]",
  },
};

export function InsightCard({
  type,
  title,
  content,
  onSave,
  onShare,
  onDeepDive,
  showSeal = true,
  className,
}: InsightCardProps) {
  const config = INSIGHT_CONFIG[type];
  const Icon = config.icon;

  return (
    <motion.div
      initial={{ opacity: 0, scale: 0.95 }}
      animate={{ opacity: 1, scale: 1 }}
      transition={{ duration: 0.3, ease: [0.16, 1, 0.3, 1] }}
      className={cn(
        "insight-card relative",
        "bg-card rounded-xl border border-border border-l-4",
        "p-5",
        config.borderColor,
        config.glowColor,
        className
      )}
      data-type={type}
    >
      {/* Seal in corner */}
      {showSeal && (
        <div className="absolute top-3 right-3">
          <InsightSeal type={type} size="sm" />
        </div>
      )}

      {/* Header */}
      <div className="flex items-start gap-3 mb-3 pr-12">
        <div className="p-2 rounded-lg bg-muted/50">
          <Icon className="w-5 h-5 text-skill-primary" />
        </div>
        <div className="flex-1">
          <SealBadge type={type} className="mb-1.5">
            {type.toUpperCase()}
          </SealBadge>
          <h3 className="font-serif font-semibold text-foreground">
            {title || config.defaultTitle}
          </h3>
        </div>
      </div>

      {/* Content */}
      <p className="text-muted-foreground whitespace-pre-wrap leading-relaxed">
        {content}
      </p>

      {/* Actions */}
      <div className="flex items-center justify-between mt-4 pt-4 border-t border-border/50">
        {/* Left: Deep dive */}
        {onDeepDive && (
          <button
            onClick={onDeepDive}
            className="text-sm font-medium text-skill-primary hover:underline"
          >
            深入了解
          </button>
        )}

        {/* Right: Save & Share */}
        <div className="flex items-center gap-2 ml-auto">
          {onSave && (
            <button
              onClick={onSave}
              className={cn(
                "inline-flex items-center gap-1.5 px-3 py-1.5",
                "text-sm rounded-full",
                "bg-muted/50 text-muted-foreground",
                "hover:bg-muted hover:text-foreground",
                "transition-colors duration-200"
              )}
            >
              <Bookmark className="w-4 h-4" />
              <span>保存</span>
            </button>
          )}
          {onShare && (
            <button
              onClick={onShare}
              className={cn(
                "inline-flex items-center gap-1.5 px-3 py-1.5",
                "text-sm rounded-full",
                "bg-muted/50 text-muted-foreground",
                "hover:bg-muted hover:text-foreground",
                "transition-colors duration-200"
              )}
            >
              <Share2 className="w-4 h-4" />
              <span>分享</span>
            </button>
          )}
        </div>
      </div>
    </motion.div>
  );
}

// ═══════════════════════════════════════════════════════════════════════════
// MiniInsightCard - Compact version for inline display
// ═══════════════════════════════════════════════════════════════════════════

interface MiniInsightCardProps {
  type: InsightType;
  title: string;
  onClick?: () => void;
  className?: string;
}

export function MiniInsightCard({
  type,
  title,
  onClick,
  className,
}: MiniInsightCardProps) {
  const config = INSIGHT_CONFIG[type];
  const Icon = config.icon;

  return (
    <motion.button
      initial={{ opacity: 0, y: 5 }}
      animate={{ opacity: 1, y: 0 }}
      onClick={onClick}
      className={cn(
        "w-full text-left p-3 rounded-lg",
        "bg-card border border-border border-l-2",
        config.borderColor,
        "hover:shadow-card-hover",
        "transition-all duration-200",
        className
      )}
    >
      <div className="flex items-center gap-2">
        <Icon className="w-4 h-4 text-skill-primary flex-shrink-0" />
        <span className="text-sm font-medium text-foreground truncate">
          {title}
        </span>
      </div>
    </motion.button>
  );
}

"use client";

import { motion } from "framer-motion";
import { cn } from "@/lib/utils";
import { SimpleVibeGlyph } from "@/components/core";

// ═══════════════════════════════════════════════════════════════════════════
// ChatMessage - LUMINOUS PAPER Design System
// 聊天消息气泡：墨色用户消息 + 卡片助手消息
// ═══════════════════════════════════════════════════════════════════════════

export interface ChatMessageProps {
  role: "user" | "assistant";
  content: string;
  timestamp?: string;
  isStreaming?: boolean;
  showAvatar?: boolean;
}

export function ChatMessage({
  role,
  content,
  timestamp,
  isStreaming,
  showAvatar = true,
}: ChatMessageProps) {
  const isUser = role === "user";

  return (
    <motion.div
      initial={{ opacity: 0, y: 10 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.3, ease: [0.16, 1, 0.3, 1] }}
      className={cn(
        "flex w-full mb-4 gap-3",
        isUser ? "flex-row-reverse" : "flex-row"
      )}
    >
      {/* Avatar */}
      {showAvatar && !isUser && (
        <div className="flex-shrink-0 w-8 h-8 flex items-center justify-center">
          <SimpleVibeGlyph size={24} skill="bazi" />
        </div>
      )}

      {/* Message bubble */}
      <div
        className={cn(
          isUser ? "chat-bubble-user" : "chat-bubble-assistant"
        )}
      >
        <p className="whitespace-pre-wrap leading-relaxed">{content}</p>

        {/* Streaming indicator */}
        {isStreaming && (
          <span className="inline-flex items-center gap-1 ml-1">
            <span className="w-1.5 h-1.5 rounded-full bg-current animate-pulse" />
            <span className="w-1.5 h-1.5 rounded-full bg-current animate-pulse animation-delay-150" />
            <span className="w-1.5 h-1.5 rounded-full bg-current animate-pulse animation-delay-300" />
          </span>
        )}

        {/* Timestamp */}
        {timestamp && (
          <p className="chat-timestamp">
            {timestamp}
          </p>
        )}
      </div>

      {/* User avatar placeholder for alignment */}
      {showAvatar && isUser && (
        <div className="flex-shrink-0 w-8 h-8" />
      )}
    </motion.div>
  );
}

// ═══════════════════════════════════════════════════════════════════════════
// SystemMessage - For system notifications in chat
// ═══════════════════════════════════════════════════════════════════════════

interface SystemMessageProps {
  content: string;
  className?: string;
}

export function SystemMessage({ content, className }: SystemMessageProps) {
  return (
    <div
      className={cn(
        "flex justify-center my-4",
        className
      )}
    >
      <span className="px-3 py-1.5 text-xs text-muted-foreground bg-muted/50 rounded-full">
        {content}
      </span>
    </div>
  );
}

// ═══════════════════════════════════════════════════════════════════════════
// TypingIndicator - Shows when assistant is thinking
// ═══════════════════════════════════════════════════════════════════════════

interface TypingIndicatorProps {
  className?: string;
}

export function TypingIndicator({ className }: TypingIndicatorProps) {
  return (
    <motion.div
      initial={{ opacity: 0, y: 10 }}
      animate={{ opacity: 1, y: 0 }}
      exit={{ opacity: 0, y: -10 }}
      className={cn("flex items-center gap-3 mb-4", className)}
    >
      <div className="flex-shrink-0 w-8 h-8 flex items-center justify-center">
        <SimpleVibeGlyph size={24} skill="bazi" />
      </div>
      <div className="chat-bubble-assistant">
        <div className="flex items-center gap-1.5">
          <span className="w-2 h-2 rounded-full bg-muted-foreground animate-bounce" style={{ animationDelay: "0ms" }} />
          <span className="w-2 h-2 rounded-full bg-muted-foreground animate-bounce" style={{ animationDelay: "150ms" }} />
          <span className="w-2 h-2 rounded-full bg-muted-foreground animate-bounce" style={{ animationDelay: "300ms" }} />
        </div>
      </div>
    </motion.div>
  );
}

"use client";

import { useState, KeyboardEvent, useRef, useEffect } from "react";
import { cn } from "@/lib/utils";
import { Send, Mic, Camera } from "lucide-react";

// ═══════════════════════════════════════════════════════════════════════════
// ChatInput - LUMINOUS PAPER Design System
// Omnibar 玻璃胶囊样式：frosted glass 输入框
// ═══════════════════════════════════════════════════════════════════════════

export interface ChatInputProps {
  onSend: (message: string) => void;
  disabled?: boolean;
  placeholder?: string;
  showMediaButtons?: boolean;
  className?: string;
}

export function ChatInput({
  onSend,
  disabled,
  placeholder = "有什么想问的...",
  showMediaButtons = false,
  className,
}: ChatInputProps) {
  const [message, setMessage] = useState("");
  const textareaRef = useRef<HTMLTextAreaElement>(null);

  // Auto-resize textarea
  useEffect(() => {
    const textarea = textareaRef.current;
    if (textarea) {
      textarea.style.height = "auto";
      textarea.style.height = `${Math.min(textarea.scrollHeight, 120)}px`;
    }
  }, [message]);

  const handleSend = () => {
    if (message.trim() && !disabled) {
      onSend(message.trim());
      setMessage("");
      // Reset height
      if (textareaRef.current) {
        textareaRef.current.style.height = "auto";
      }
    }
  };

  const handleKeyDown = (e: KeyboardEvent<HTMLTextAreaElement>) => {
    if (e.key === "Enter" && !e.shiftKey) {
      e.preventDefault();
      handleSend();
    }
  };

  const canSend = message.trim().length > 0 && !disabled;

  return (
    <div className={cn("p-4 md:px-6", className)}>
      <div className="omnibar">
        {/* Media buttons (optional) */}
        {showMediaButtons && (
          <>
            <button
              type="button"
              className="omnibar-btn"
              aria-label="语音输入"
            >
              <Mic className="w-5 h-5" />
            </button>
            <button
              type="button"
              className="omnibar-btn"
              aria-label="拍照上传"
            >
              <Camera className="w-5 h-5" />
            </button>
          </>
        )}

        {/* Input */}
        <textarea
          ref={textareaRef}
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          onKeyDown={handleKeyDown}
          placeholder={placeholder}
          disabled={disabled}
          rows={1}
          className="omnibar-input"
          style={{ minHeight: "24px", maxHeight: "120px" }}
        />

        {/* Send button */}
        <button
          type="button"
          onClick={handleSend}
          disabled={!canSend}
          className={cn(
            "omnibar-send",
            !canSend && "opacity-50 cursor-not-allowed"
          )}
          aria-label="发送消息"
        >
          <Send className="w-5 h-5" />
        </button>
      </div>

      {/* Hint text */}
      <p className="text-center text-xs text-muted-foreground mt-2">
        按 Enter 发送，Shift+Enter 换行
      </p>
    </div>
  );
}

// ═══════════════════════════════════════════════════════════════════════════
// QuickReplies - Suggested quick reply buttons
// ═══════════════════════════════════════════════════════════════════════════

interface QuickRepliesProps {
  replies: string[];
  onSelect: (reply: string) => void;
  className?: string;
}

export function QuickReplies({ replies, onSelect, className }: QuickRepliesProps) {
  if (replies.length === 0) return null;

  return (
    <div className={cn("flex flex-wrap gap-2 px-4 pb-2", className)}>
      {replies.map((reply, index) => (
        <button
          key={index}
          onClick={() => onSelect(reply)}
          className={cn(
            "px-3 py-1.5 text-sm rounded-full",
            "bg-muted/50 text-muted-foreground",
            "border border-border/50",
            "hover:bg-muted hover:text-foreground",
            "transition-colors duration-200"
          )}
        >
          {reply}
        </button>
      ))}
    </div>
  );
}

// ═══════════════════════════════════════════════════════════════════════════
// FloatingOmnibar - Floating version for landing pages
// ═══════════════════════════════════════════════════════════════════════════

interface FloatingOmnibarProps {
  onSend: (message: string) => void;
  placeholder?: string;
  className?: string;
}

export function FloatingOmnibar({
  onSend,
  placeholder = "输入你的出生日期开始...",
  className,
}: FloatingOmnibarProps) {
  const [value, setValue] = useState("");

  const handleSubmit = () => {
    if (value.trim()) {
      onSend(value.trim());
      setValue("");
    }
  };

  return (
    <div className={cn("glass-card p-6 max-w-lg mx-auto", className)}>
      <div className="omnibar">
        <input
          type="text"
          value={value}
          onChange={(e) => setValue(e.target.value)}
          onKeyDown={(e) => e.key === "Enter" && handleSubmit()}
          placeholder={placeholder}
          className="omnibar-input"
        />
        <button
          onClick={handleSubmit}
          disabled={!value.trim()}
          className={cn(
            "omnibar-send",
            !value.trim() && "opacity-50 cursor-not-allowed"
          )}
        >
          <Send className="w-5 h-5" />
        </button>
      </div>
    </div>
  );
}

