### 4. ChatInput.tsx - 输入框组件

**位置**: `/home/aiscend/work/vibelife/apps/web/src/components/chat/ChatInput.tsx`

```tsx
"use client";

import { useState, KeyboardEvent, useRef, useEffect } from "react";
import { cn } from "@/lib/utils";
import { Send, Mic, Camera } from "lucide-react";

// ═══════════════════════════════════════════════════════════════════════════
// ChatInput - LUMINOUS PAPER Design System
// Omnibar 玻璃胶囊样式：frosted glass 输入框
// ═══════════════════════════════════════════════════════════════════════════

export interface ChatInputProps {
  onSend: (message: string) => void;
  disabled?: boolean;
  placeholder?: string;
  showMediaButtons?: boolean;
  className?: string;
}

export function ChatInput({
  onSend,
  disabled,
  placeholder = "有什么想问的...",
  showMediaButtons = false,
  className,
}: ChatInputProps) {
  const [message, setMessage] = useState("");
  const textareaRef = useRef<HTMLTextAreaElement>(null);

  // Auto-resize textarea
  useEffect(() => {
    const textarea = textareaRef.current;
    if (textarea) {
      textarea.style.height = "auto";
      textarea.style.height = `${Math.min(textarea.scrollHeight, 120)}px`;
    }
  }, [message]);

  const handleSend = () => {
    if (message.trim() && !disabled) {
      onSend(message.trim());
      setMessage("");
      // Reset height
      if (textareaRef.current) {
        textareaRef.current.style.height = "auto";
      }
    }
  };

  const handleKeyDown = (e: KeyboardEvent<HTMLTextAreaElement>) => {
    if (e.key === "Enter" && !e.shiftKey) {
      e.preventDefault();
      handleSend();
    }
  };

  const canSend = message.trim().length > 0 && !disabled;

  return (
    <div className={cn("p-4 md:px-6", className)}>
      <div className="omnibar">
        {/* Media buttons (optional) */}
        {showMediaButtons && (
          <>
            <button
              type="button"
              className="omnibar-btn"
              aria-label="语音输入"
            >
              <Mic className="w-5 h-5" />
            </button>
            <button
              type="button"
              className="omnibar-btn"
              aria-label="拍照上传"
            >
              <Camera className="w-5 h-5" />
            </button>
          </>
        )}

        {/* Input */}
        <textarea
          ref={textareaRef}
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          onKeyDown={handleKeyDown}
          placeholder={placeholder}
          disabled={disabled}
          rows={1}
          className="omnibar-input"
          style={{ minHeight: "24px", maxHeight: "120px" }}
        />

        {/* Send button */}
        <button
          type="button"
          onClick={handleSend}
          disabled={!canSend}
          className={cn(
            "omnibar-send",
            !canSend && "opacity-50 cursor-not-allowed"
          )}
          aria-label="发送消息"
        >
          <Send className="w-5 h-5" />
        </button>
      </div>

      {/* Hint text */}
      <p className="text-center text-xs text-muted-foreground mt-2">
        按 Enter 发送，Shift+Enter 换行
      </p>
    </div>
  );
}
