            <span className="w-1.5 h-1.5 rounded-full bg-current animate-pulse animation-delay-150" />
            <span className="w-1.5 h-1.5 rounded-full bg-current animate-pulse animation-delay-300" />
          </span>
        )}

        {/* Timestamp */}
        {timestamp && (
          <p className="chat-timestamp">
            {timestamp}
