  disabled,
  placeholder = "有什么想问的...",
  showMediaButtons = false,
  className,
}: ChatInputProps) {
  const [message, setMessage] = useState("");
  const textareaRef = useRef<HTMLTextAreaElement>(null);

  // Auto-resize textarea
  useEffect(() => {
    const textarea = textareaRef.current;
    if (textarea) {
      textarea.style.height = "auto";
      textarea.style.height = `${Math.min(textarea.scrollHeight, 120)}px`;
    }
  }, [message]);

  const handleSend = () => {
    if (message.trim() && !disabled) {
      onSend(message.trim());
      setMessage("");
      // Reset height
      if (textareaRef.current) {
        textareaRef.current.style.height = "auto";
      }
    }
  };

  const handleKeyDown = (e: KeyboardEvent<HTMLTextAreaElement>) => {
    if (e.key === "Enter" && !e.shiftKey) {
      e.preventDefault();
      handleSend();
    }
  };

  const canSend = message.trim().length > 0 && !disabled;

  return (
    <div className={cn("p-4 md:px-6", className)}>
      <div className="omnibar">
        {/* Media buttons (optional) */}
        {showMediaButtons && (
          <>
            <button
              type="button"
              className="omnibar-btn"
              aria-label="语音输入"
            >
              <Mic className="w-5 h-5" />
            </button>
            <button
              type="button"
              className="omnibar-btn"
              aria-label="拍照上传"
            >
              <Camera className="w-5 h-5" />
            </button>
          </>
        )}

        {/* Input */}
        <textarea
          ref={textareaRef}
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          onKeyDown={handleKeyDown}
          placeholder={placeholder}
          disabled={disabled}
          rows={1}
          className="omnibar-input"
          style={{ minHeight: "24px", maxHeight: "120px" }}
        />

        {/* Send button */}
        <button
          type="button"
          onClick={handleSend}
          disabled={!canSend}
          className={cn(
            "omnibar-send",
            !canSend && "opacity-50 cursor-not-allowed"
          )}
          aria-label="发送消息"
        >
          <Send className="w-5 h-5" />
        </button>
      </div>

      {/* Hint text */}
      <p className="text-center text-xs text-muted-foreground mt-2">
        按 Enter 发送，Shift+Enter 换行
      </p>
    </div>
  );
}
```

**输入框样式** (在 globals.css 中定义):
```css
.omnibar {
  @apply glass-capsule flex items-center gap-3 px-4 py-3;
}

.omnibar-input {
  @apply flex-1 bg-transparent border-none outline-none
         text-foreground placeholder:text-muted-foreground
         resize-none;
}

.omnibar-btn {
  @apply p-2 rounded-full text-muted-foreground
         hover:bg-muted/50 hover:text-foreground
         transition-colors duration-200;
}

.omnibar-send {
  @apply p-2 rounded-full bg-ink text-vellum
         hover:bg-ink/90 active:scale-95
         transition-all duration-200;
}

/* Glass Capsule */
.glass-capsule {
  @apply backdrop-blur-xl rounded-full;
  background: hsl(var(--card) / 0.9);
  border: 1px solid hsl(var(--border) / 0.4);
  box-shadow:
    0 4px 20px rgba(28, 26, 23, 0.08),
    inset 0 1px 0 rgba(255, 255, 255, 0.1);
}
