"use client";

import { useState, useRef, useEffect } from "react";
import { ChatMessage } from "./ChatMessage";
import { ChatInput } from "./ChatInput";
import { InsightCard, InsightType } from "@/components/insight/InsightCard";
import { getTokens, sendGuestMessage, sendMessage, ChatResponse } from "@/lib/api";

interface Message {
  id: string;
  role: "user" | "assistant";
  content: string;
  timestamp?: string;
  insight?: {
    id: string;
    insight_type: InsightType;
    title: string;
    content: string;
  };
}

export interface ChatContainerProps {
  skillId: string;
  conversationId?: string;
  onConversationStart?: (id: string) => void;
}

export function ChatContainer({ skillId, conversationId, onConversationStart }: ChatContainerProps) {
  const [messages, setMessages] = useState<Message[]>([]);
  const [isLoading, setIsLoading] = useState(false);
  const [currentConvoId, setCurrentConvoId] = useState(conversationId);
  const messagesEndRef = useRef<HTMLDivElement>(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const handleSend = async (content: string) => {
    // Add user message
    const userMessage: Message = {
      id: Date.now().toString(),
      role: "user",
      content,
      timestamp: new Date().toLocaleTimeString(),
    };
    setMessages((prev) => [...prev, userMessage]);
    setIsLoading(true);

    try {
      const { accessToken } = getTokens();

      if (accessToken) {
        const response: ChatResponse = await sendMessage({
          message: content,
          skill_id: skillId,
          conversation_id: currentConvoId,
        });

        // Update conversation ID
        if (!currentConvoId && response.conversation_id) {
          setCurrentConvoId(response.conversation_id);
          onConversationStart?.(response.conversation_id);
        }

        // Add assistant message with potential insight
        const assistantMessage: Message = {
          id: (Date.now() + 1).toString(),
          role: "assistant",
          content: response.content,
          timestamp: new Date().toLocaleTimeString(),
          insight: response.insight ? {
            id: response.insight.id,
            insight_type: response.insight.insight_type as InsightType,
            title: response.insight.title,
            content: response.insight.content,
          } : undefined,
        };
        setMessages((prev) => [...prev, assistantMessage]);
      } else {
        // Guest mode: use the public endpoint for progressive disclosure
        const response = await sendGuestMessage(content, skillId);
        const assistantMessage: Message = {
          id: (Date.now() + 1).toString(),
          role: "assistant",
          content: response.content,
          timestamp: new Date().toLocaleTimeString(),
        };
        setMessages((prev) => [...prev, assistantMessage]);

        if (response.suggestion) {
          setMessages((prev) => [
            ...prev,
            {
              id: (Date.now() + 2).toString(),
              role: "assistant",
              content: response.suggestion,
              timestamp: new Date().toLocaleTimeString(),
            },
          ]);
        }
      }
    } catch (error) {
      console.error("Chat error:", error);
      // Add error message
      setMessages((prev) => [...prev, {
        id: (Date.now() + 1).toString(),
        role: "assistant",
        content: "抱歉，出了点问题。请稍后再试。",
      }]);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="flex flex-col h-full">
      {/* Messages */}
      <div className="flex-1 overflow-y-auto p-4">
        {messages.length === 0 && (
          <div className="text-center text-gray-500 py-12">
            <p className="text-lg mb-2">开始对话吧</p>
            <p className="text-sm">有什么我可以帮到你的？</p>
          </div>
        )}
        {messages.map((message) => (
          <div key={message.id}>
            <ChatMessage
              role={message.role}
              content={message.content}
              timestamp={message.timestamp}
            />
            {/* Show insight card if present */}
            {message.insight && (
              <div className="ml-12 mt-2 mb-4">
                <InsightCard
                  type={message.insight.insight_type}
                  title={message.insight.title}
                  content={message.insight.content}
                  onSave={() => console.log("Save insight:", message.insight?.id)}
                  onShare={() => console.log("Share insight:", message.insight?.id)}
                />
              </div>
            )}
          </div>
        ))}
        {isLoading && (
          <ChatMessage
            role="assistant"
            content=""
            isStreaming
          />
        )}
        <div ref={messagesEndRef} />
      </div>

      {/* Input */}
      <ChatInput onSend={handleSend} disabled={isLoading} />
    </div>
  );
}
