"use client"

import { useState, useRef, useCallback } from "react"
import { motion, AnimatePresence } from "framer-motion"
import { Mic, Camera, Send, X, Loader2 } from "lucide-react"
import { cn } from "@/lib/utils"

interface OmnibarProps {
  onSubmit: (content: string) => void
  placeholder?: string
  disabled?: boolean
  isLoading?: boolean
  className?: string
}

export function Omnibar({
  onSubmit,
  placeholder = "有什么想告诉我的...",
  disabled = false,
  isLoading = false,
  className,
}: OmnibarProps) {
  const [value, setValue] = useState("")
  const [isRecording, setIsRecording] = useState(false)
  const inputRef = useRef<HTMLInputElement>(null)

  const handleSubmit = useCallback(() => {
    if (!value.trim() || disabled || isLoading) return
    onSubmit(value.trim())
    setValue("")
  }, [value, disabled, isLoading, onSubmit])

  const handleKeyDown = (e: React.KeyboardEvent) => {
    if (e.key === "Enter" && !e.shiftKey) {
      e.preventDefault()
      handleSubmit()
    }
  }

  const handleVoiceClick = () => {
    setIsRecording(!isRecording)
    // TODO: 实现语音录制
  }

  const handleCameraClick = () => {
    // TODO: 实现拍照功能
  }

  return (
    <motion.div
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.4, ease: [0.16, 1, 0.3, 1] }}
      className={cn(
        "glass-card rounded-full flex items-center px-3 py-2 sm:px-4 sm:py-3 gap-2 sm:gap-3",
        "transition-all duration-300",
        "focus-within:ring-2 focus-within:ring-accent/20",
        disabled && "opacity-50 pointer-events-none",
        className
      )}
    >
      {/* Voice Button */}
      <motion.button
        type="button"
        onClick={handleVoiceClick}
        whileHover={{ scale: 1.1 }}
        whileTap={{ scale: 0.95 }}
        className={cn(
          "p-2 rounded-full transition-colors",
          isRecording
            ? "bg-destructive/10 text-destructive"
            : "hover:bg-accent/10 text-foreground-muted hover:text-foreground"
        )}
        aria-label={isRecording ? "停止录音" : "开始录音"}
      >
        <AnimatePresence mode="wait">
          {isRecording ? (
            <motion.div
              key="recording"
              initial={{ scale: 0 }}
              animate={{ scale: 1 }}
              exit={{ scale: 0 }}
            >
              <X className="w-5 h-5" />
            </motion.div>
          ) : (
            <motion.div
              key="mic"
              initial={{ scale: 0 }}
              animate={{ scale: 1 }}
              exit={{ scale: 0 }}
            >
              <Mic className="w-5 h-5" />
            </motion.div>
          )}
        </AnimatePresence>
      </motion.button>

      {/* Camera Button */}
      <motion.button
        type="button"
        onClick={handleCameraClick}
        whileHover={{ scale: 1.1 }}
        whileTap={{ scale: 0.95 }}
        className="p-2 rounded-full hover:bg-accent/10 text-foreground-muted hover:text-foreground transition-colors"
        aria-label="拍照"
      >
        <Camera className="w-5 h-5" />
      </motion.button>

      {/* Divider */}
      <div className="w-px h-6 bg-border" />

      {/* Text Input */}
      <input
        ref={inputRef}
        type="text"
        value={value}
        onChange={(e) => setValue(e.target.value)}
        onKeyDown={handleKeyDown}
        placeholder={placeholder}
        disabled={disabled || isLoading}
        className={cn(
          "flex-1 bg-transparent outline-none min-w-0",
          "text-foreground placeholder:text-foreground-muted",
          "text-sm sm:text-base"
        )}
      />

      {/* Submit Button */}
      <motion.button
        type="button"
        onClick={handleSubmit}
        disabled={!value.trim() || disabled || isLoading}
        whileHover={{ scale: 1.05 }}
        whileTap={{ scale: 0.95 }}
        className={cn(
          "p-2 rounded-full transition-all duration-200",
          value.trim() && !disabled && !isLoading
            ? "bg-foreground text-background hover:opacity-90"
            : "bg-muted text-muted-foreground cursor-not-allowed"
        )}
        aria-label="发送"
      >
        <AnimatePresence mode="wait">
          {isLoading ? (
            <motion.div
              key="loading"
              initial={{ opacity: 0, rotate: 0 }}
              animate={{ opacity: 1, rotate: 360 }}
              exit={{ opacity: 0 }}
              transition={{ repeat: Infinity, duration: 1, ease: "linear" }}
            >
              <Loader2 className="w-4 h-4" />
            </motion.div>
          ) : (
            <motion.div
              key="send"
              initial={{ scale: 0 }}
              animate={{ scale: 1 }}
              exit={{ scale: 0 }}
            >
              <Send className="w-4 h-4" />
            </motion.div>
          )}
        </AnimatePresence>
      </motion.button>
    </motion.div>
  )
}

"use client"

import { motion } from "framer-motion"
import { Flame, Zap, Sparkles, Settings } from "lucide-react"
import { cn } from "@/lib/utils"

type StatusBarProps = {
  energy: number
  momentum: number
  aura: string
  streakDays?: number
}

const AURA_LABELS: Record<string, { label: string; color: string }> = {
  calm: { label: "平静", color: "text-blue-500" },
  alert: { label: "警觉", color: "text-red-500" },
  happy: { label: "愉悦", color: "text-yellow-500" },
  anxious: { label: "焦虑", color: "text-purple-500" },
  flow: { label: "心流", color: "text-green-500" },
}

export function TopStatusBar({ energy, momentum, aura, streakDays = 0 }: StatusBarProps) {
  const auraStyle = AURA_LABELS[aura] || AURA_LABELS.calm

  return (
    <motion.div
      initial={{ opacity: 0, y: -20 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.4, ease: [0.16, 1, 0.3, 1] }}
      className={cn(
        "glass sticky top-0 z-40",
        "flex items-center justify-between",
        "px-4 py-3",
        "border-b border-border/50"
      )}
    >
      {/* Left - Stats */}
      <div className="flex items-center gap-4 sm:gap-6">
        {/* Momentum */}
        <motion.div
          className="flex items-center gap-1.5"
          whileHover={{ scale: 1.05 }}
        >
          <div className="flex items-center justify-center w-7 h-7 rounded-full bg-accent/10">
            <Flame className="w-4 h-4 text-accent" />
          </div>
          <div className="flex flex-col">
            <span className="text-xs text-foreground-muted leading-none">动量</span>
            <span className="text-sm font-semibold text-foreground">{momentum}</span>
          </div>
        </motion.div>

        {/* Energy */}
        <motion.div
          className="flex items-center gap-1.5"
          whileHover={{ scale: 1.05 }}
        >
          <div className="flex items-center justify-center w-7 h-7 rounded-full bg-blue-500/10">
            <Zap className="w-4 h-4 text-blue-500" />
          </div>
          <div className="flex flex-col">
            <span className="text-xs text-foreground-muted leading-none">能量</span>
            <span className="text-sm font-semibold text-foreground">{energy}</span>
          </div>
        </motion.div>

        {/* Streak (Mobile hidden) */}
        {streakDays > 0 && (
          <div className="hidden sm:flex items-center gap-1.5">
            <span className="text-sm">🔥</span>
            <span className="text-sm font-medium text-foreground">Day {streakDays}</span>
          </div>
        )}
      </div>

      {/* Right - Aura & Settings */}
      <div className="flex items-center gap-3">
        {/* Aura Badge */}
        <motion.div
          initial={{ scale: 0.9 }}
          animate={{ scale: 1 }}
          className={cn(
            "inline-flex items-center gap-1.5 px-3 py-1.5 rounded-full",
            "bg-muted text-sm"
          )}
        >
          <Sparkles className={cn("w-3.5 h-3.5", auraStyle.color)} />
          <span className={cn("font-medium", auraStyle.color)}>{auraStyle.label}</span>
        </motion.div>

        {/* Settings */}
        <motion.button
          whileHover={{ scale: 1.1, rotate: 90 }}
          whileTap={{ scale: 0.95 }}
          transition={{ duration: 0.2 }}
          className="p-2 rounded-full hover:bg-muted transition-colors"
        >
          <Settings className="w-4 h-4 text-foreground-muted" />
        </motion.button>
      </div>
    </motion.div>
  )
}
