   157→        {/* Central glyph */}
   158→        <VibeGlyph
   159→          size="sm"
   160→          skill={skill}
   161→          showAura={true}
   162→          animate={true}
   163→          className="relative z-10"
   164→        />
   165→      </div>
   166→
