"use client";

import { useState, useRef, useEffect, useMemo } from "react";
import { ChatMessage } from "./ChatMessage";
import { ChatInput } from "./ChatInput";
import { InsightCard, InsightType } from "@/components/insight/InsightCard";
import { VibeGlyph, BreathAura, type SkillType } from "@/components/core";
import { DailyGreeting } from "@/components/greeting/DailyGreeting";
import { getTokens, sendGuestMessage, streamChat } from "@/lib/api";

interface Message {
  id: string;
  role: "user" | "assistant";
  content: string;
  timestamp?: string;
  insight?: {
    id: string;
    insight_type: InsightType;
    title: string;
    content: string;
  };
}

export type VoiceMode = "warm" | "sarcastic";

export interface ChatContainerProps {
  skillId: string;
  skill?: SkillType;
  conversationId?: string;
  voiceMode?: VoiceMode;
  onConversationStart?: (id: string) => void;
}

// ═══════════════════════════════════════════════════════════════════════════
// ChatEmptyState - LUMINOUS PAPER Design System
// 空状态：居中 VibeGlyph + 技能感知文案 + 呼吸光晕
// ═══════════════════════════════════════════════════════════════════════════

const SKILL_EMPTY_STATE: Record<SkillType, { title: string; subtitle: string }> = {
  bazi: {
    title: "探索你的命理",
    subtitle: "分享你的生辰，开启八字解读之旅",
  },
  zodiac: {
    title: "倾听星辰的声音",
    subtitle: "让星盘为你揭示宇宙的密语",
  },
  mbti: {
    title: "发现真实的自己",
    subtitle: "通过对话，探索你的人格特质",
  },
  attach: {
    title: "理解你的依恋模式",
    subtitle: "探索亲密关系中的自我",
  },
  career: {
    title: "规划你的职业蓝图",
    subtitle: "让我们一起探索你的职业发展方向",
  },
};

// Quick prompts for each skill
const SKILL_QUICK_PROMPTS: Record<SkillType, string[]> = {
  bazi: [
    "我是1990年5月15日早上8点出生的",
    "帮我分析今年的运势",
    "我的事业运如何？",
    "什么时候适合做重大决定？",
  ],
  zodiac: [
    "我是双子座，帮我看看本周运势",
    "我的太阳星座和上升星座有什么区别？",
    "双子座和什么星座最配？",
    "水逆对我有什么影响？",
  ],
  mbti: [
    "帮我测测我的MBTI类型",
    "INFP适合什么职业？",
    "我是INTJ，如何改善人际关系？",
    "E人和I人有什么本质区别？",
  ],
  attach: [
    "帮我分析我的依恋类型",
    "回避型依恋如何建立亲密关系？",
    "为什么我总是害怕被抛弃？",
    "如何治愈童年的情感创伤？",
  ],
  career: [
    "我该如何规划职业发展？",
    "我适合创业还是打工？",
    "如何在面试中展现自己？",
    "转行需要考虑什么？",
  ],
};

// 生成当日问候数据
function useDailyGreeting() {
  return useMemo(() => {
    const now = new Date();
    const hour = now.getHours();

    // 基于时间的问候语
    const greetings = {
      morning: "早安，愿你今日能量满满",
      afternoon: "午后时光，适合沉淀思考",
      evening: "晚间静谧，回顾今日收获",
    };

    let greeting = greetings.morning;
    if (hour >= 12 && hour < 18) greeting = greetings.afternoon;
    else if (hour >= 18) greeting = greetings.evening;

    // 节气（简化版，实际应根据日期计算）
    const solarTerms = ["小寒", "大寒", "立春", "雨水", "惊蛰", "春分"];
    const termIdx = Math.floor((now.getMonth() * 2 + Math.floor(now.getDate() / 15)) % 6);

    return {
      greeting,
      solarTerm: solarTerms[termIdx],
      termDescription: "万物生长",
      todayTip: "今日宜专注当下，一步一脚印",
    };
  }, []);
}

function ChatEmptyState({ skill, onQuickPrompt }: { skill: SkillType; onQuickPrompt?: (prompt: string) => void }) {
  const config = SKILL_EMPTY_STATE[skill] || SKILL_EMPTY_STATE.bazi;
  const quickPrompts = SKILL_QUICK_PROMPTS[skill] || SKILL_QUICK_PROMPTS.bazi;
  const dailyData = useDailyGreeting();

  return (
    <div className="flex flex-col items-center justify-center max-w-xl mx-auto px-4 py-6 w-full">
      {/* Daily Greeting 每日问候 */}
      <DailyGreeting
        greeting={dailyData.greeting}
        solarTerm={dailyData.solarTerm}
        termDescription={dailyData.termDescription}
        todayTip={dailyData.todayTip}
        className="mb-6 w-full"
      />

      {/* Animated glyph with enhanced aura */}
      <div className="relative flex items-center justify-center mb-4">
        {/* Outer pulsing ring */}
        <div className="absolute w-16 h-16 rounded-full border border-skill-primary/20 animate-pulse-slow" />
        <div className="absolute w-24 h-24 rounded-full border border-skill-primary/10 animate-pulse-slower" />

        {/* Breath aura */}
        <BreathAura
          skill={skill}
          size="sm"
          position="center"
          intensity="medium"
          className="opacity-40"
        />

        {/* Central glyph */}
        <VibeGlyph
          size="sm"
          skill={skill}
          showAura={true}
          animate={true}
          className="relative z-10"
        />
      </div>

      {/* Title */}
      <h3 className="text-base md:text-lg font-serif font-semibold text-foreground mb-1 text-center">
        {config.title}
      </h3>

      {/* Subtitle */}
      <p className="text-sm text-muted-foreground max-w-sm text-center mb-4">
        {config.subtitle}
      </p>

      {/* Quick prompt suggestions */}
      <div className="w-full space-y-2">
        <p className="text-xs text-muted-foreground/60 text-center mb-2">
          试试这样问
        </p>
        <div className="grid grid-cols-1 sm:grid-cols-2 gap-2">
          {quickPrompts.slice(0, 4).map((prompt, index) => (
            <button
              key={index}
              onClick={() => onQuickPrompt?.(prompt)}
              className="group relative px-3 py-2 text-left text-sm text-muted-foreground
                       bg-card hover:bg-card/80 border border-border/50 hover:border-border
                       rounded-lg transition-all duration-200
                       hover:shadow-sm"
            >
              <span className="line-clamp-2">{prompt}</span>
            </button>
          ))}
        </div>
      </div>
    </div>
  );
}

export function ChatContainer({
  skillId,
  skill = "bazi",
  conversationId,
  voiceMode = "warm",
  onConversationStart,
}: ChatContainerProps) {
  const [messages, setMessages] = useState<Message[]>([]);
  const [isLoading, setIsLoading] = useState(false);
  const [currentConvoId, setCurrentConvoId] = useState(conversationId);
  const messagesEndRef = useRef<HTMLDivElement>(null);

  // Map skillId to SkillType if not provided
  const activeSkill: SkillType = skill || (skillId as SkillType) || "bazi";

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const handleSend = async (content: string) => {
    // Add user message
    const userMessage: Message = {
      id: Date.now().toString(),
      role: "user",
      content,
      timestamp: new Date().toLocaleTimeString(),
    };
    setMessages((prev) => [...prev, userMessage]);
    setIsLoading(true);

    try {
      const { accessToken } = getTokens();

      if (accessToken) {
        // Create placeholder for streaming response
        const assistantMsgId = (Date.now() + 1).toString();
        setMessages((prev) => [...prev, {
          id: assistantMsgId,
          role: "assistant",
          content: "",
          timestamp: new Date().toLocaleTimeString(),
        }]);

        // Use streaming API
        await streamChat(
          {
            message: content,
            skill: skillId,
            conversation_id: currentConvoId,
            voice_mode: voiceMode,
          },
          // onChunk
          (chunk) => {
            setMessages((prev) =>
              prev.map((msg) =>
                msg.id === assistantMsgId
                  ? { ...msg, content: msg.content + chunk }
                  : msg
              )
            );
          },
          // onDone
          (result) => {
            if (!currentConvoId && result.conversation_id) {
              setCurrentConvoId(result.conversation_id);
              onConversationStart?.(result.conversation_id);
            }
            setIsLoading(false);
          },
          // onError
          (error) => {
            console.error("Stream error:", error);
            setMessages((prev) =>
              prev.map((msg) =>
                msg.id === assistantMsgId
                  ? { ...msg, content: "抱歉，出了点问题。请稍后再试。" }
                  : msg
              )
            );
            setIsLoading(false);
          }
        );
        return; // Early return since streaming handles setIsLoading
      } else {
        // Guest mode: use the public endpoint for progressive disclosure
        const response = await sendGuestMessage(content, skillId);
        const assistantMessage: Message = {
          id: (Date.now() + 1).toString(),
          role: "assistant",
          content: response.content,
          timestamp: new Date().toLocaleTimeString(),
        };
        setMessages((prev) => [...prev, assistantMessage]);

        if (response.suggestion) {
          setMessages((prev) => [
            ...prev,
            {
              id: (Date.now() + 2).toString(),
              role: "assistant",
              content: response.suggestion,
              timestamp: new Date().toLocaleTimeString(),
            },
          ]);
        }
      }
    } catch (error) {
      console.error("Chat error:", error);
      setMessages((prev) => [...prev, {
        id: (Date.now() + 1).toString(),
        role: "assistant",
        content: "抱歉，出了点问题。请稍后再试。",
      }]);
    } finally {
      setIsLoading(false);
    }
  };

  const hasMessages = messages.length > 0;

  return (
    <div className="flex flex-col h-full" data-skill={activeSkill}>
      {/* Messages area - always flex-1 to push input to bottom */}
      <div className="flex-1 overflow-y-auto relative min-h-0">
        {!hasMessages ? (
          /* Empty state - centered within scrollable area */
          <div className="flex flex-col items-center justify-end h-full pb-4 px-4">
            <ChatEmptyState skill={activeSkill} onQuickPrompt={handleSend} />
          </div>
        ) : (
          /* Messages list */
          <div className="px-4 pt-4 pb-2">
            {messages.map((message) => (
              <div key={message.id}>
                <ChatMessage
                  role={message.role}
                  content={message.content}
                  timestamp={message.timestamp}
                />
                {/* Show insight card if present */}
                {message.insight && (
                  <div className="ml-12 mt-2 mb-4">
                    <InsightCard
                      type={message.insight.insight_type}
                      title={message.insight.title}
                      content={message.insight.content}
                      onSave={() => console.log("Save insight:", message.insight?.id)}
                      onShare={() => console.log("Share insight:", message.insight?.id)}
                    />
                  </div>
                )}
              </div>
            ))}
            {isLoading && (
              <ChatMessage
                role="assistant"
                content=""
                isStreaming
              />
            )}
            <div ref={messagesEndRef} />
          </div>
        )}
      </div>

      {/* Input - fixed at bottom */}
      <div className="flex-shrink-0">
        <ChatInput onSend={handleSend} disabled={isLoading} />
      </div>
    </div>
  );
}

