input.function:
() => {
  const nav = document.querySelector('nav');
  const main = document.querySelector('main');
  const aside = document.querySelector('aside') || document.querySelector('[role="complementary"]');

  return {
    viewport: { width: window.innerWidth, height: window.innerHeight },
    navigation: nav ? {
      width: nav.offsetWidth,
      position: window.getComputedStyle(nav).position,
      left: nav.offsetLeft,
    } : null,
    main: main ? {
      width: main.offsetWidth,
      marginLeft: window.getComputedStyle(main).marginLeft,
      marginRight: window.getComputedStyle(main).marginRight,
      maxWidth: window.getComputedStyle(main).maxWidth,
      position: window.getComputedStyle(main).position,
      left: main.offsetLeft,
    } : null,
    aside: aside ? {
      width: aside.offsetWidth,
      position: window.getComputedStyle(aside).position,
      right: aside.offsetLeft,
    } : null,
  };
}

