{
  "$schema": "https://turbo.build/schema.json",
  "globalDependencies": [".env"],
  "tasks": {
    "build": {
      "dependsOn": ["^build"],
      "outputs": [".next/**", "!.next/cache/**", "dist/**"]
    },
    "dev": {
      "cache": false,
      "persistent": true
    },
    "start": {
      "dependsOn": ["build"]
    },
    "lint": {
      "dependsOn": ["^build"]
    },
    "typecheck": {
      "dependsOn": ["^build"]
    },
    "test": {
      "dependsOn": ["build"],
      "outputs": ["coverage/**"]
    },
    "clean": {
      "cache": false
    }
  }
}

"use client";

import { useState } from "react";
import Link from "next/link";
import { Button } from "@/components/ui/Button";
import { Card } from "@/components/ui/Card";
import { invokeGuestTool, GuestToolResponse } from "@/lib/api";

interface ToolResult {
  rawResult: string;
  interpretation: string;
  chartData?: Record<string, unknown>;
}

export default function BaziLandingPage() {
  const [birthDate, setBirthDate] = useState("");
  const [birthTime, setBirthTime] = useState("");
  const [result, setResult] = useState<ToolResult | null>(null);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  const handleQuickAnalysis = async () => {
    if (!birthDate) return;

    setIsLoading(true);
    setError(null);
    setResult(null);

    try {
      const response = await invokeGuestTool({
        skill_id: "bazi",
        tool: "bazi_chart",
        birth_date: birthDate,
        birth_time: birthTime || undefined,
      });

      if (response.success) {
        setResult({
          rawResult: response.raw_result || "",
          interpretation: response.interpretation || "",
          chartData: response.chart_data,
        });
      } else {
        setError(response.error || "分析失败");
      }
    } catch (err) {
      setError(err instanceof Error ? err.message : "分析失败，请稍后再试");
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <main className="min-h-screen bg-gradient-to-br from-amber-50 to-yellow-50 dark:from-gray-900 dark:to-amber-950">
      {/* Header */}
      <header className="fixed top-0 left-0 right-0 z-50 bg-white/80 dark:bg-gray-900/80 backdrop-blur border-b border-amber-200 dark:border-amber-900">
        <div className="max-w-6xl mx-auto px-4 h-16 flex items-center justify-between">
          <Link href="/" className="flex items-center gap-2">
            <span className="text-2xl">☯️</span>
            <span className="font-bold text-amber-800 dark:text-amber-200">八字命理</span>
          </Link>
          <div className="flex items-center gap-4">
            <Link href="/bazi/chat" className="text-amber-700 dark:text-amber-300 hover:underline">
              深度对话
            </Link>
            <Link href="/auth/login">
              <Button size="sm">登录</Button>
            </Link>
          </div>
        </div>
      </header>

      {/* Hero */}
      <section className="pt-32 pb-16 px-4">
        <div className="max-w-3xl mx-auto text-center animate-fade-in">
          <h1 className="text-5xl font-bold mb-4 text-amber-900 dark:text-amber-100">
            知命 · 造命
          </h1>
          <p className="text-xl text-amber-700 dark:text-amber-300 mb-8">
            通过八字了解你的天赋与人生节奏
          </p>

          {/* Quick Analysis Form */}
          <Card className="max-w-md mx-auto bg-white/80 dark:bg-gray-800/80">
            <h3 className="font-semibold mb-4 text-amber-800 dark:text-amber-200">
              快速体验
            </h3>
            <div className="space-y-4">
              <div>
                <label className="block text-sm text-gray-600 dark:text-gray-400 mb-1">
                  出生日期
                </label>
                <input
                  type="date"
                  value={birthDate}
                  onChange={(e) => setBirthDate(e.target.value)}
                  className="w-full px-4 py-2 rounded-lg border border-amber-200 dark:border-amber-800 bg-white dark:bg-gray-900"
                />
              </div>
              <div>
                <label className="block text-sm text-gray-600 dark:text-gray-400 mb-1">
                  出生时辰（可选）
                </label>
                <select
                  value={birthTime}
                  onChange={(e) => setBirthTime(e.target.value)}
                  className="w-full px-4 py-2 rounded-lg border border-amber-200 dark:border-amber-800 bg-white dark:bg-gray-900"
                >
                  <option value="">不确定</option>
                  <option value="子时(23:00-01:00)">子时 (23:00-01:00)</option>
                  <option value="丑时(01:00-03:00)">丑时 (01:00-03:00)</option>
                  <option value="寅时(03:00-05:00)">寅时 (03:00-05:00)</option>
                  <option value="卯时(05:00-07:00)">卯时 (05:00-07:00)</option>
                  <option value="辰时(07:00-09:00)">辰时 (07:00-09:00)</option>
                  <option value="巳时(09:00-11:00)">巳时 (09:00-11:00)</option>
                  <option value="午时(11:00-13:00)">午时 (11:00-13:00)</option>
                  <option value="未时(13:00-15:00)">未时 (13:00-15:00)</option>
                  <option value="申时(15:00-17:00)">申时 (15:00-17:00)</option>
                  <option value="酉时(17:00-19:00)">酉时 (17:00-19:00)</option>
                  <option value="戌时(19:00-21:00)">戌时 (19:00-21:00)</option>
                  <option value="亥时(21:00-23:00)">亥时 (21:00-23:00)</option>
                </select>
              </div>
              <Button
                onClick={handleQuickAnalysis}
                className="w-full bg-amber-600 hover:bg-amber-700"
                isLoading={isLoading}
                disabled={!birthDate}
              >
                查看命盘
              </Button>
            </div>
          </Card>

          {/* Error */}
          {error && (
            <div className="mt-6 p-4 bg-red-50 dark:bg-red-900/30 rounded-lg text-red-700 dark:text-red-300">
              {error}
            </div>
          )}

          {/* Result */}
          {result && (
            <div className="mt-8 animate-slide-up space-y-6">
              {/* Chart Data Display */}
              <Card className="text-left bg-white/90 dark:bg-gray-800/90">
                <h4 className="font-semibold text-amber-800 dark:text-amber-200 mb-4">
                  八字命盘
                </h4>
                <pre className="whitespace-pre-wrap text-sm text-gray-700 dark:text-gray-300 font-mono bg-amber-50 dark:bg-gray-900 p-4 rounded-lg overflow-x-auto">
                  {result.rawResult}
                </pre>
              </Card>

              {/* Interpretation */}
              {result.interpretation && (
                <Card className="text-left bg-gradient-to-br from-amber-50 to-yellow-50 dark:from-amber-900/30 dark:to-yellow-900/30">
                  <h4 className="font-semibold text-amber-800 dark:text-amber-200 mb-3">
                    AI 解读
                  </h4>
                  <p className="text-gray-700 dark:text-gray-300 leading-relaxed">
                    {result.interpretation}
                  </p>
                </Card>
              )}

              {/* CTA */}
              <Card className="bg-white/90 dark:bg-gray-800/90">
                <div className="text-center">
                  <p className="text-amber-700 dark:text-amber-300 mb-4">
                    想要了解更多？注册后可获得：
                  </p>
                  <ul className="text-sm text-gray-600 dark:text-gray-400 mb-6 space-y-1">
                    <li>✓ 大运流年详细分析</li>
                    <li>✓ 十神格局深度解读</li>
                    <li>✓ 事业/感情/健康个性化建议</li>
                    <li>✓ 无限次对话咨询</li>
                  </ul>
                  <Link href="/auth/register">
                    <Button className="bg-amber-600 hover:bg-amber-700">
                      注册获取完整解读
                    </Button>
                  </Link>
                </div>
              </Card>
            </div>
          )}
        </div>
      </section>

      {/* Features */}
      <section className="py-16 px-4 bg-white/50 dark:bg-gray-800/50">
        <div className="max-w-4xl mx-auto">
          <h2 className="text-2xl font-bold text-center mb-8 text-amber-800 dark:text-amber-200">
            八字能告诉你什么？
          </h2>
          <div className="grid md:grid-cols-3 gap-6">
            <Card variant="bordered">
              <div className="text-3xl mb-3">🎯</div>
              <h3 className="font-semibold mb-2">性格天赋</h3>
              <p className="text-sm text-gray-600 dark:text-gray-400">
                了解你的天性、优势和潜在挑战
              </p>
            </Card>
            <Card variant="bordered">
              <div className="text-3xl mb-3">📅</div>
              <h3 className="font-semibold mb-2">运势节奏</h3>
              <p className="text-sm text-gray-600 dark:text-gray-400">
                把握大运流年，顺势而为
              </p>
            </Card>
            <Card variant="bordered">
              <div className="text-3xl mb-3">💡</div>
              <h3 className="font-semibold mb-2">人生指引</h3>
              <p className="text-sm text-gray-600 dark:text-gray-400">
                事业、感情、健康的方向建议
              </p>
            </Card>
          </div>
        </div>
      </section>

      {/* Footer */}
      <footer className="py-8 text-center text-amber-700 dark:text-amber-400 text-sm">
        <p>© 2026 VibeLife · 八字命理</p>
        <p className="mt-1">
          <Link href="/" className="hover:underline">返回 VibeLife</Link>
        </p>
      </footer>
    </main>
  );
}

