    44â†’  skill = "bazi",
    45â†’  size = "md",
    46â†’  label,
    47â†’  className,
    48â†’}: InsightSealProps) {
    49â†’  const config = typeConfig[type];
    50â†’  const displayLabel = label || config.label;
    51â†’  const shape = skillShapes[skill];
    52â†’
    53â†’  return (
    54â†’    <div
    55â†’      className={cn(
    56â†’        "insight-seal relative inline-flex items-center justify-center",
    57â†’        "font-serif font-bold text-antique-gold",
    58â†’        "border-2 border-antique-gold/30",
    59â†’        "bg-gradient-to-br from-antique-gold/10 to-transparent",
    60â†’        sizeClasses[size],
    61â†’        shape === "circle" && "rounded-full",
    62â†’        shape === "square" && "rounded-sm",
    63â†’        shape === "hexagon" && "rounded-lg",
    64â†’        shape === "diamond" && "rounded-md rotate-45",
    65â†’        className
    66â†’      )}
    67â†’      style={{ transform: `rotate(-12deg)` }}
    68â†’    >
    69â†’      {/* Inner border */}
    70â†’      <div
    71â†’        className={cn(
    72â†’          "absolute inset-1 border border-antique-gold/20",
    73â†’          shape === "circle" && "rounded-full",
    74â†’          shape === "square" && "rounded-[2px]",
    75â†’          shape === "hexagon" && "rounded-md",
    76â†’          shape === "diamond" && "rounded-sm"
    77â†’        )}
    78â†’      />
    79â†’
    80â†’      {/* Label */}
    81â†’      <span
    82â†’        className={cn(
    83â†’          "relative z-10 tracking-wider",
    84â†’          shape === "diamond" && "-rotate-45"
    85â†’        )}
    86â†’      >
    87â†’        {displayLabel.slice(0, 4)}
    88â†’      </span>
    89â†’    </div>
    90â†’  );
    91â†’}
    92â†’
    93â†’// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
    94â†’// PersonalSeal - User's unique seal based on birth data
    95â†’// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
    96â†’
    97â†’interface PersonalSealProps {
    98â†’  birthYear?: number;
    99â†’  birthMonth?: number;
   100â†’  birthDay?: number;
   101â†’  skill?: SkillType;
   102â†’  size?: "sm" | "md" | "lg";
   103â†’  className?: string;
   104â†’}
   105â†’
   106â†’// Generate a deterministic pattern from birth data
   107â†’function generateSealPattern(year: number, month: number, day: number): string {
   108â†’  const seed = year * 10000 + month * 100 + day;
   109â†’  const chars = "ABCDEFGHJKLMNPQRSTUVWXYZ";
   110â†’  let result = "";
   111â†’  let n = seed;
   112â†’  for (let i = 0; i < 2; i++) {
   113â†’    result += chars[n % chars.length];
   114â†’    n = Math.floor(n / chars.length);
   115â†’  }
   116â†’  return result;
   117â†’}
   118â†’
   119â†’export function PersonalSeal({
   120â†’  birthYear = 1990,
   121â†’  birthMonth = 1,
   122â†’  birthDay = 1,
   123â†’  skill = "bazi",
   124â†’  size = "md",
   125â†’  className,
   126â†’}: PersonalSealProps) {
   127â†’  const pattern = generateSealPattern(birthYear, birthMonth, birthDay);
   128â†’
   129â†’  return (
   130â†’    <InsightSeal
   131â†’      skill={skill}
   132â†’      size={size}
   133â†’      label={pattern}
   134â†’      className={className}
   135â†’    />
   136â†’  );
   137â†’}
   138â†’
   139â†’// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
   140â†’// SealBadge - Inline badge with seal styling
   141â†’// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
   142â†’
   143â†’interface SealBadgeProps {
