   151â†’  pattern: "bg-zodiac-primary/10 text-zodiac-primary border-zodiac-primary/20",
   152â†’  timing: "bg-antique-gold/10 text-antique-gold border-antique-gold/20",
   153â†’  growth: "bg-element-wood/10 text-element-wood border-element-wood/20",
   154â†’};
   155â†’
